@-moz-document regexp("^https?://www\\.google\\.[a-z\\\\.]+/\\_/chrome/newtab.*") {
  #hplogo,
  #lga::before,
  #lga::after {
    background-repeat: no-repeat;
    /*outline: 1px solid red;*/
  }

  #hplogo {
    background-image: url(https://lh5.googleusercontent.com/-4cZfL3iCfZQ/U4CdGn260xI/AAAAAAAAEMs/8kQeLqURdyc/s393/Logo.png);
    width: 0;
    height: 0;
    padding: 63.5px 196.5px !important;
  }

  #lga {
    margin: 70px 0 30px;
    height: auto;
  }

  #lga::before,
  #lga::after {
    background-image: url(https://lh3.googleusercontent.com/-wEjFO1AqpyE/U380skKt__I/AAAAAAAAEMc/p6xS9T62id0/s1780/chars.png);
    content: "";
    display: block;
    position: absolute;
    top: 100px;
    margin: 0;
    pointer-events: none;
    z-index: 10;
    height: 480px;
  }

  #lga::before {
    /*[[left]]*/
    /* https://github.com/JasonBarnabe/csspool/issues/10 */
    left: -webkit-calc(20% - 124px);
    left:         calc(20% - 124px);
    /*left: -webkit-calc((100% - 620px) / 5);
    left:         calc((100% - 620px) / 5);*/
  }

  #lga::after {
    /*[[right]]*/
    /* https://github.com/JasonBarnabe/csspool/issues/10 */
    right: -webkit-calc(20% - 124px);
    right:         calc(20% - 124px);
    /*right: -webkit-calc((100% - 620px) / 5);
    right:         calc((100% - 620px) / 5);*/
  }
}

@-moz-document domain("freestyler.ws"){html{margin:8px!important}body{display:none!important}html::before,html::after{display:block!important}html::before{content:"FreeStyler has been discontinued"!important;font-size:2em!important;font-weight:bold!important;margin:.67em 0!important}html::after{content:"If you're looking for user styles, visit userstyles.org."!important;margin:1em 0!important}}
