css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document  url-prefix("http://docs.unity3d.com"),
                url-prefix("https://docs.unity3d.com"),
                regexp('file:///.*Unity.*/Documentation/.*')
{
    h1{
       font-size : 1.5em;
    }
    h2{
        font-size : 1.2em;
    }
    table.list td{
        padding : 0px;
    }
    
    table.list tr:last-child td, table.list tr:first-child td{
        padding : 0px !important;
    }
    
    div.master-wrapper{
       margin-left : 0px;
       padding-left : 12px;
    }
    
    ._mCS_1[style]{
        height : /*[[sideMenuHeight]]*/ !important;
    }
    
    div.sidebar-wrap{
        width : 280px;
        
    }
    div.mCustomScrollBox li{
        padding-left : 18px !important;
    }
    div.sidebar-menu{
        margin-bottom : 4px;
        padding-bottom : 4px;
    }
    div.sidebar-menu ul li ul{
        margin-top : 0px;
    }
    div.sidebar-menu ul li, div.sidebar-menu ul li ul li{
       font-size : 93.5%; 
        margin-bottom : 0px;
    }
    div.sidebar div.content{
        padding-right : 0px;
    }
    
    .footer-wrapper{
        clear : both;
    }
    
   
}
