css:
@-moz-document domain("physicsoverflow.org") {
/*MODERN FONT*/ 
* {font-family:/*[[font]]*/, sans-serif !important;}

/*FLAT DESIGN*/

/**BUTTON REDESIGN**/
.po-button-new, .qa-form-wide-button, .qa-form-tall-button, #qa-login {
  box-shadow:none; 
  border:none; 
  border-radius:0px !important;
  background-image:none;
} 
.po-button-new:hover, .qa-form-tall-button:hover, .qa-form-wide-button:hover, #qa-login:hover {
  background-color:#00DCDC; 
  background-image:none;
}   
.qa-form-tall-button-cancel:hover {
  background-color:#FF0000;
  border:none;    
}          
#qa-login {
  background-color:#0179B5;
}
/**TAG REDESIGN**/
.qa-tag-link, .qa-widget-side-high div a {
  border:none !important; 
  border-radius:0px; 
  transition: all ease-in-out 0s !important;
} 
.qa-tag-link:hover, .qa-widget-side-high div a:hover {
  background-color:/****#C6E6F6****/ #00DCDC;
} 
/***FIXES***/
li.qa-related-q-item a:hover {
 background-color:transparent !important;
}

/**EDITOR REDESIGN**/ 
.cke_top, .cke_toolgroup, a.cke_combo_button {
  background-image:-webkit-gradient(linear,left top,left bottom,from(#ffffff),to(#ffffff)) !important;
}     
.cke_toolgroup {
  margin:0 0 0 0; 
  border:none; 
  border-radius:none;
} 
a.cke_button:hover, a.cke_combo_button:hover {
  background-color:#bbddff !important; 
  background-image:none !important;
}  

/**TABLE REDESIGN**/
table tr:hover {background-color:transparent;}
}
