css:
@-moz-document url-prefix('http://new.my.yahoo.co.jp/'), url-prefix('http://my.yahoo.co.jp/') {

    /* 標準セット
    ------------------------------- */

    #wrapper #contents .search {
        min-width: 1024px;
    }
    #wrapper #layoutBox {
        min-width: 1004px;
    }




    /* センタリング
    ------------------------------ */

    /*[[center]]*/ #wrapper #layoutBox {
        max-width: 1004px;
    }




    /* 検索BOXを上に
    ------------------------------ */

    /*[[sb_top]]*/ #wrapper #header {
        position: relative;
        min-width: 1004px;
        border-bottom-color: rgba(0, 0, 0, 0);
    }
    /*[[sb_top]]*/ #wrapper #header .leftBox {
        position: absolute;
        left: 50%;
        margin-left: -512px;
    }
    /*[[sb_top]]*/ #wrapper #header .menu {
        position: absolute;
        right: 50%;
        margin-right: -512px;
    }
    /*[[sb_top]]*/ #wrapper #contents .search {
        max-width: 1024px;
        padding-bottom: 10px;
    }
    /*[[sb_top]]*/ #wrapper #contents {
        position: relative;
    }
    /*[[sb_top]]*/ #wrapper #contents:before {
        content: "";
        display: block;
        position: absolute;
        top: -10px;
        width: 100%;
        height: 49px;
        background: rgba(255, 255, 255, 0.4);
    }

    /*[[sb_top]]*/ #wrapper #header {
        position: relative;
        min-height: 100px;
    }
    /*[[sb_top]]*/ #wrapper #header h1 a {
        margin-top: 8px;
    }
    /*[[sb_top]]*/ #wrapper #header .menu {
        position: absolute;
        top: 96px;
        z-index: 1;
    }
    /*[[sb_top]]*/ #wrapper .popupPosition {
        top: 120px;
        left: -138px;
    }
    /*[[sb_top]]*/ #wrapper #contents .search {
        position: relative;
        padding-top: 29px;
    }
    /*[[sb_top]]*/ #wrapper #contents .searchBox {
        position: absolute;
        top: -94px;
        left: 160px;
    }
    /*[[sb_top]]*/ #wrapper .topTxt .tabSelect {
        position: absolute;
        top: -75px;
        z-index: 1;
    }
    /*[[sb_top]]*/ #wrapper .pulldown {
        border-style: solid;
        border-color: #CCC;
        border-width: 1px;
    }
    /*[[sb_top]]*/  #wrapper .topTxt .links {
        position: absolute;
        margin-left: 0;
        top: -22px;
    }





    /* いろんな所をスリムにする
    ------------------------------ */

    /*[[ntc_hide]]*/ #wrapper .searchBox .noticeBox {
        visibility: hidden;
        width: 132px;
        height: 1px;
        overflow: hidden;
    }

    /*[[sb_slim]]*/ #wrapper .Ysearch {
        padding-top: 10px;
    }
    
    /*[[sb_slim]]*/ #wrapper .searchForm .txtForm {
        font-size: 12px;
        height: 28px;
        line-height: 28px;
    }

    /*[[sb_slim]]*/ #wrapper .searchForm .cmnSubBtn {
        font-size: 12px;
        height: 28px;
        line-height: 28px;
    }

    /*[[sb_slim]]*/ .searchForm .del {
        top: 3px;
    }

    /*[[lbl_slim]]*/ #wrapper .labelL {
        padding-top: 1px;
        padding-bottom: 0;
    }

    /*[[lbl_slim]]*/ #wrapper .labelL .labelIcons {
        transform-origin: 100% 50%;
        -webkit-transform: scale(0.8, 0.8);
        -moz-transform: scale(0.8, 0.8);
        -o-transform: scale(0.8, 0.8);
        -ms-transform: scale(0.8, 0.8);
        transform: scale(0.8, 0.8);
    }
}
