css:
@-moz-document domain("meteofrance.com") {
body
{ background: black !important; margin-top: 5px !important;}

  #block-bandeauadministrablepoi /* Incitation à installer l'appli */
, #block-publicmenusocialheader /* Boutons rézosocio en haut de page */
, #primary_sidebar /* "Infos" parasites */
, #block-autopromorain /* Incitation à installer l'appli */
, #primary_bottom
, #secondary_section
, #block-publicmenusocial /* Boutons rézosocio en bas de page */
, #tertiary_section /* Vidéos */
, #quinary_section, #sixary_section /* Dossiers/reportages divers */
, #block-mfpoifooter /* Top 10 des villes les plus consultées */
{ display: none !important;}

#main_container {
    width: 900px;
}
#primary_content {
    width: 100% !important;
}

/* Réduire la hauteur (extravagante) de la météo du jour */
#atmogramme_slider,
#atmogramme_slider .slider_item.single
{ min-height: 180px;}
}
