css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("https://mail.google.com/mail/u/0/")
{
body, td, input, textarea, select {
    font-family: Tempus Sans ITC;
}
.lpb {
    text-align: center;
    width: 320px;
    border: 1px rgb(153, 153, 153);
    padding: 4px;
	height: 9px;
    margin-right: auto;
    margin-left: auto;
    box-shadow: 0px 0px 3px /*[[fgcolor]]*/ ;
    border-radius: 15px;
	}
#lpt {
    border-radius: 15px;
    background-color: /*[[bgcolor]]*/ ;
}
.msg {
margin-top: 250px;
text-align: center;
padding-bottom: 10px;
  text-align: center;
  font-family:Tempus Sans ITC;
  font-size: 1.5em;
  color: #F7F7F7 !important;;
  letter-spacing: 0.01em;
         text-shadow:
    0 0 2px rgba(204, 208, 212,0.9),
    0 10px 20px rgba(0, 0, 0, 0.3),
    0 -2px 3px rgba(0, 0, 0, 0.1),
    0 -5px 10px rgba(255, 255, 255, 0.5),
    0 5px 10px rgba(0, 0, 0, 0.3),
    0 3px 4px rgba(255, 255, 255, 0.2),
    0 0 20px rgba(255, 255, 255, 0.45);
  
    animation: loading 0.45s ease-in-out infinite alternate;
}
@keyframes loading {
	to {text-shadow:
    0 0 2px rgba(204, 208, 212,0.2),
    0 0 3px rgba(0, 0, 0, 0.02),
    0 0 0 rgba(0, 0, 0, 0),
    0 0 0 rgba(255, 255, 255, 0),
    0 0 0 rgba(0, 0, 0, 0),
    0 0 0 rgba(255, 255, 255, 0),
    0 0 0 rgba(255, 255, 255, 0);}
}
}
@-moz-document url("https://mail.google.com/mail/u/0/#inbox") {
body, td, input, textarea, select {
    margin: 0px;
    font-family: arial,sans-serif;
}
   
}
