css:
@-moz-document domain("qq.com"){

/* 
Author: Wayzlin
Version: 1.4.3
Update Date: 2020/03/14
*/


/*屏蔽*/


/*logo*/

.logo {
    display: none !important;
}


/*广告*/

#div_corner_ad_container,
#QM_Container_11.collet_box.fn_paipai,
#QM_Container_100005,
li.f-single.feed-hot,
div.act_hz.bg.bor2.act_hz_dark,
div.bor3.panel.panel_gg.qzone-display,
div.bor3.panel.photo_panel_photoapp.qzone-display,
#blocked_visitor,
a[href="http://pay.qq.com/qzone/index.shtml?aid=photo.upload"],
#js-sidebar-ctn #js-qq-ad,
.pop_apps_wrap,
#idQbossHotbar,
#bottom-sponsor,
div.layout-app div.qz-box.fn-box-popularize,
#appstore_sidebar_l,
#appstore_sidebar_r,
[id^=fullcol-banner-],
div.mall-sp-container.mall-sp-container-small,
.sidebar.gdtads_box,
#qz_notification,
#visitorsDiv,
#qbossLogo_tip>div.logo-tips,
#qbossLogo_tip>div.dropdown-line,
#ICFeedsContainer>div.sidebar.lite_hide>div.word_spread.side_panel,
#ICFeedsContainer div[class="split_line bor2"],
#ICFeedsContainer>div.sidebar.lite_hide>a,
div.main_content.main_login div.add_friend_access.access_option div,
#adImg,
#login_div>div>div.linwei-wrap>div.linwei-area>span,
#login_div>div>div.login_head>h1>a.linwei-logo>i,
li[class="f-single f-s-s f-single-biz"] {
    display: none !important;
}


/*昵称*/

.user-home span.user-name.textoverflow,
span[class="user-name textoverflow"],
#msgList div.box.bgr3>div.bd>a.qz_311_author.c_tx.nickname.goProfile,
#headContainer>div.head-name {
    display: none !important;
}


/*新*/

.icon-new-fun,
.icon-red-dot {
    display: none !important;
}


/*赞*/

.profile-hd-actions {
    display: none !important;
}


/*个人中心签到*/

#QM_Container_31 {
    display: none !important;
}


/*个人中心大家都在看*/

#QM_Container_100002 {
    display: none !important;
}


/*个人中心可能认识的人*/

#QM_Container_100003 {
    display: none !important;
}


/*个人中心礼物*/

#QM_Container_333 {
    display: none !important;
}


/*个人中心那年今日*/

#QM_Container_100014,
div#today_lastyear_v2 {
    display: none !important;
}


/*个人中心猜你喜欢*/

#QM_Container_100012 {
    display: none !important;
}


/*个人中心左侧应用*/

.mod-side-nav.mod-side-nav-recently-used,
#tab_menu_list li[type="app"] {
    display: none !important;
}


/*个人中心被隐藏的选项*/

#tab_switch,
#tab_hide_list,
.top-toggle {
    display: none !important;
}


/*个人中心快速插图*/

.qimg-trigger,
.qimg-trigger-collapsed {
    display: none !important;
}


/*个人中心左边栏吃喝玩乐*/

a#tab_menu_dianping {
    display: none !important;
}


/*个人中心左边栏QQ演出*/

a#tab_menu_yanchu {
    display: none !important;
}


/*个人中心左边栏那年今日*/

a#tab_menu_sdc {
    display: none !important;
}


/*个人中心左边栏微云备份*/

a#tab_menu_weiyun {
    display: none !important;
}


/*个人中心左边栏视频广场*/

a#tab_menu_mv {
    display: none !important;
}


/*开通黄钻*/

.fn_accessLog_tips,
.user-vip-info,
.infomation p,
#recent_visitors_side,
p.right,
a.open_vip_ad,
#vipBottomAdContainer,
div.mod_ad.showInNoVip.none,
p.light,
a.mod-open-vip,
div#js-capacity-droplist,
p[style="top:13px;"],
#paperLayoutNotice,
#visitMeContainer div.user-name-bg a,
#visitYouContainer div.user-name-bg a,
.head-detail-info.clearfix,
#headContainer>div.head-detail,
[id^=qz_poster_v4_editor_container_]>div.qz-poster-attach-side>div.attach>div.item.bor3.bg4.item-funny>i,
#main>div.vip-info-bar.continue-vip,
div.vip-info-bar.continue-vip {
    display: none !important;
}

.vip-setting,
#openVipButton,
#renewVipButton {
    visibility: hidden !important;
}


/*等级图标*/

.qz_qzone_lv_s,
.qz_qzone_lv,
.qz-ichotclick,
.qz-f-vip-l,
.icon-wrap,
a.qz_vip_icon_s.qz_vip_icon_fla_s_1,
#sTitle,
.flower_info {
    display: none !important;
}


/*头像装饰*/

.qz_emoji,
a.f-user-hat,
#_qzone_cards>a,
#top_head_title>span.qz-app-flag.wing-fly {
    display: none !important;
}


/*名片黄钻会员等级标识*/

#_namecard_feed>div.qzone-cards-app a.nickname-ico,
#qzNameCardDiv a[class^="qz_vip_icon_s"],
div.f-single-head.f-aside>div.user-info>div.f-nick>a.user-medal {
    display: none !important;
}


/*生活记录与好友日志*/

a[href="http://rc.qzone.qq.com/blog?catalog=ugc"],
a[href="http://rc.qzone.qq.com/blog?catalog=friend"] {
    display: none !important;
}


/*说说墙*/

div.mod_wrap_inner li.nav_wall {
    display: none !important;
}


/*修正相册评论头像显示错误*/

div.figure-interactive div.mod_comments_sub div.ui_avatar,
#js-comment-module>div>div>div>ul>li:nth-child(1n)>div>div.mod_comments_sub>ol>li div.ui_avatar {
    display: none !important;
}


/*留言板诈骗提醒*/

#cheatHintArea {
    display: none !important;
}


/*视频热门分享*/

#app_mod>div.wrap>div.aside.col_lar.bg3 {
    display: none !important;
}


/*顶部和热点*/

[id^=qz_notification_],
#site_hot_btn,
body>div.fix-layout,
#QZ_Body>div.fix-layout {
    display: none !important;
}


/*底部版权信息*/

.layout-copyright,
.lay_copyright,
.page_foot.big_page,
body>div>div.footer,
#lay>div.lay_foot>div.lay_inner>div.copyright,
#lay>div.lay_foot>div.author-info {
    visibility: hidden !important;
}


/*开通空间*/

#top_tips_container,
#top_tips_seat {
    display: none !important;
}

.top-fix-inner {
    margin-top: 0 !important;
}


/*热门直播*/

#QM_Container_HotLive {
    display: none !important;
}


/*腾讯课堂*/

#tab_menu_list>li.ke_gray {
    display: none !important;
}



/*美化*/


/*去除下划线*/

* {
    text-decoration: none !important;
}


/*更改字体*/

* {
    font-family: 微软雅黑!important;
}


/*[[beijing]]*/


/*图片圆角*/

li[id^=fct_] img,
a.img-two,
a.img-three,
li[id^=fct_] div.img-box,
div.img-attachments.img_thumb.imgscale-big.smallPic.pic img,
[id^=hex_]>div a {
    border-radius: 6px;
}

.feed .f-item .f-ct .img-box-row-wrap .img-num {
    border-radius: 6px;
    border-top-left-radius: 6px;
    border-top-right-radius: 6px;
    border-bottom-right-radius: 6px;
    border-bottom-left-radius: 6px;
}


/*版头美化*/


/*[[daohanglan]]*/


/*头像位置*/

#layBackground .head-avatar {
    width: 100% !important;
    text-align: center;
    top: -270px !important;
}

#layBackground .head-avatar div {
    width: 40% !important;
    margin: 0 30% 0 30%;
    text-align: center;
}


/*标题位置*/

#headContainer>div.head-info,
#headContainer>div.head-info h1 {
    width: 100% !important;
    text-align: center;
    top: 150px !important;
}

#headContainer>div.head-info>div.qz-title-editor {
    width: 15% !important;
    margin: 0 42.5% 0 42.5%;
    text-align: center;
}

#headContainer>div.head-info div span {
    width: 30% !important;
    margin: 0 35% 0 35%;
    text-align: center;
}

#headContainer>div.head-info>div.dragdrop_item_title>a {
    width: 2% !important;
    margin: 0 40% 0 58%;
    text-align: center;
}


/*版头高度*/

#headContainer {
    height: 280px !important;
}


/*模块*/


/*个人中心好友动态区*/

#main_feed_container,
#pageContent,
#care_friend_container {
    width: 100%;
}

#main_feed_container>div.col-main-feed,
#care_friend_container>div.col-main-feed {
    width: 50% !important;
    margin: 0 25% 0 25%;
    box-shadow: 0px 0px 1px rgba(0, 0, 0, 0.2)!important;
    border-radius: 5px !important;
    padding: 25px;
    border: solid #fff 1px;
    position: relative !important;
    left: -20px;
}

.feed-v9 .f-single {
    width: auto !important;
}

.feed-v9 .f-single .fui-imgbox-row-wrap .img-box-row .img-num {
    right: auto !important;
}


/*访客*/

div.bg_mode div.box_ml.bor3 {
    width: 800px!important;
    padding: 25px !important;
    box-shadow: 0px 0px 1px rgba(0, 0, 0, 0.2)!important;
    border-radius: 5px!important;
    position: relative !important;
    left: 50px !important;
}


/*特别关心栏*/

#care_friend_container>div.col-main-feed {
    width: 50% !important;
    margin: 0 25% 0 25%;
    box-shadow: 0px 0px 1px rgba(0, 0, 0, 0.2)!important;
    border-radius: 5px!important;
    padding: 25px;
    border: solid #fff 1px;
    position: relative !important;
    left: -20px;
}


/*主页左边栏*/

#pageContent.body-grid {
    width: 100% !important;
}


/*主页右边栏*/

#pageContent>div.qz-main.profile-main {
    width: 50% !important;
    margin: 0 25% 0 25%;
    box-shadow: 0px 0px 1px rgba(0, 0, 0, 0.2)!important;
    border-radius: 5px !important;
    padding: 25px;
    border: solid #fff 1px;
    position: relative !important;
    left: -20px;
}


/*个人中心选项标签*/

.qz-grid {
    border-radius: 10px;
}

div.mod-side-nav:nth-child(1) {
    position: fixed !important;
    left: 5px !important;
    top: 390px !important;
    padding: 20px 0px 15px 15px !important;
    box-shadow: 0px 0px 1px rgba(0, 0, 0, 0.2)!important;
    width: 170px !important;
    transition: height 0.5s, margin 0.5s;
    margin-left: -150px!important;
    border-radius: 5px 5px 5px 5px !important;
}

div.mod-side-nav:nth-child(1):hover {
    height: auto !important;
    width: 140px !important;
    transition: height 0.5s, margin 0.5s;
    margin-right: 0 !important;
    margin-left: -15px!important;
    border-radius: 5px 5px 5px 5px !important;
}

#tab_menu_friend>div.qz-left>i,
#tab_menu_care>div.qz-left>i,
#tab_menu_me>div.qz-left>i {
    position: fixed;
    left: 5px;
}

#tab_menu_friend>div.qz-left>i {
    position: fixed;
    top: 422px;
}

#tab_menu_care>div.qz-left>i {
    position: fixed;
    top: 457px;
}

#tab_menu_me>div.qz-left>i {
    position: fixed;
    top: 492px;
}

span.sn-title {
    position: relative;
    left: 20px;
}


/*悬停*/

#QM_Container_3,
#care_friend_container>div.col-main-sidebar {
    position: fixed !important;
    right: 5px !important;
    bottom: -12px !important;
    padding: 30px 5px 0px 25px !important;
    box-shadow: 0px 0px 1px rgba(0, 0, 0, 0.2)!important;
    width: 210px !important;
    height: 0!important;
    transition: height 0.5s, margin 0.5s;
    margin-right: -220px!important;
    background-color: #fff;
    border-radius: 25px 5px 0 0!important;
}

#QM_Container_3:hover,
#care_friend_container>div.col-main-sidebar:hover {
    width: 230px !important;
    height: auto !important;
    transition: height 0.5s, margin 0.5s;
    margin-right: 0 !important;
    border-radius: 5px 5px 0 0!important;
}

.icenter-right-mod {
    border-style: none !important;
}

#pageContent>div.qz-left.profile-left {
    position: fixed !important;
    left: 5px !important;
    bottom: -12px !important;
    padding: 30px 25px 0 5px!important;
    box-shadow: 0px 0px 1px rgba(0, 0, 0, 0.2)!important;
    width: 210px !important;
    height: 0!important;
    transition: height 0.5s, margin 0.5s;
    margin-left: -220px!important;
    background-color: #fff;
    border-radius: 5px 25px 0 0 !important;
}

#pageContent>div.qz-left.profile-left:hover {
    width: 288px !important;
    height: auto !important;
    transition: height 0.5s, margin 0.5s;
    margin-left: 0 !important;
    border-radius: 5px 5px 0 0!important;
}

.icenter-right-mod {
    border-style: none !important;
}


/*[[touxiang]]*/
}
