css:
@-moz-document domain("wikipedia.org"),
domain("wikimedia.org"),
domain("wiktionary.org") {

/*
ADDITIONS
*/

/*[[toc]]*/
/*[[wales]]*/
/*[[sitenotice]]*/

/*
HEAD
*/

    /*[[header]]*/
    #ca-watch {
        background: url(data:image/png;base64,) no-repeat center !important;
        padding-top: 10px !important;
    }
    #ca-unwatch {
        background: url(data:image/png;base64,) no-repeat center !important;
        padding-top: 10px !important;
    }
    #ca-wikilove.icon a {
        padding: 1.1em 0.5em 0 !important;
        height: auto !important;
        padding-top: 1.1em !important;
        width: auto !important;
    }
    div.vectorMenu h3 span {
        padding-top: 1.25em !important;
    }
    #mw-head {
        position: relative !important;
        max-width: 923px !important;
        height: 90px !important;
        margin: 0 auto !important;
    }
    #mw-head a,
    #mw-head ul,
    #mw-head span,
    #mw-head .vectorTabs {
        background: none !important;
        border: none !important;
    }
    div.vectorTabs ul li {
        border: none !important;
        background: none !important;
        background-color: #e2e0de !important;
    }
    #mw-head > * {
        position: absolute !important;
        margin: 0 !important;
        padding: 0 !important;
    }
    #mw-head #left-navigation {
        top: 50px !important;
        left: 29px !important;
        padding: 0 !important;
    }
    #mw-head #right-navigation {
        top: 50px !important;
        right: 0 !important;
        padding: 0 !important;
    }
    #mw-head #p-personal {
        top: 0px !important;
        right: 0 !important;
        padding: 5px 30px 0 0 !important;
        font-size: 14px !important;
    }
    #mw-head a,
    #mw-head a:visited {
        color: #556 !important;
    }
    #mw-head #searchform > div {
        -webkit-border-radius: 7px;
        -moz-border-radius: 7px;
        border-radius: 7px;
        position: absolute !important;
        top: -32px !important;
        right: 30px !important;
        margin-top: 13px !important;
        margin-left: 15px !important;
        border: none !important;
        background: #E8E6E2 !important;
        width: 350px !important;
        padding: 7px 0 0 !important;
    }
    #mw-head #searchform > div input#searchInput {
        padding: 0px 0 0px 8px !important;
    }
    div#mw-head div.vectorMenu h3 {
        background-image: none !important;
    }
    #pt-userpage,
    #pt-anonuserpage,
    #pt-login {
        background-image: none !important;
    }
    @font-face {
        font-family: 'Cinzel Decorative';
        font-style: normal;
        font-weight: 700;
        src: local('Cinzel Decorative Bold'), local('CinzelDecorative-Bold'), url(https://fonts.gstatic.com/s/cinzeldecorative/v4/pXhIVnhFtL_B9Vb1wq2F98yeMSefaLEdHpYZZALYSS_3rGVtsTkPsbDajuO5ueQw.woff2) format('woff2');
        unicode-range: U+0000-00FF, U+0131, U+0152-0153, U+02C6, U+02DA, U+02DC, U+2000-206F, U+2074, U+20AC, U+2212, U+2215;
    }
    #mw-head:before {
        position: relative !important;
        width: 100px !important;
        height: 100px !important;
        top: 31px !important;
        left: 33px !important;
        content: '/*[[wikipedia]]*/' !important;
        font-family: 'Cinzel Decorative', serif !important;
        font-weight: bold !important;
        font-size: 26px !important;
        letter-spacing: 0px !important;
        color: #000 !important;
        z-index: 1 !important;
    }
    #pt-notifications-alert > a, #pt-notifications-notice > a {
        color: #e2e0de !important;
    }
    #p-page2 > h3 > a,
    #p-user2 > h3 > a {
        background-image: url(https://en.wikipedia.org/w/skins/Vector/images/arrow-down-icon.png?d72f0) !important;
        background-repeat: no-repeat !important;
        background-position: 100% 70% !important;
    }

/*
NAVIGATION
*/
    
    #mw-panel {
        z-index: 01189998819991197253 !important;
    }
    #mw-navigation #mw-panel {
        position: fixed !important;
        font-size: 18px !important;
        top: 0px !important;
        left: -245px !important;
        width: 190px !important;
        height: 100% !important;
        overflow-y: auto !important;
        padding: 0 0 0 100px !important;
        background: #E8E6E2 !important;
        box-shadow: 1px 0 3px rgba(0, 0, 0, 0.25) !important;
        transition: all
        /*[[anim]]*/
        
        ease-in-out !important;
        opacity: 0 !important;
    }
    #mw-navigation #mw-panel:hover {
        left: -100px !important;
        transition: all
        /*[[anim]]*/
        
        ease-in-out !important;
        opacity: 1 !important;
    }
    #mw-navigation #mw-panel a {
        color: #445 !important;
    }
    #mw-navigation #p-navigation,
    #mw-navigation .first {
        margin-bottom: 0 !important;
        margin-top: 7px !important;
    }
    #mw-navigation #p-lang:before {
        content: "" !important;
        font-size: 0 !important;
        display: inline-block !important;
        height: 0px !important;
        width: 100% !important;
        border-top: 1px solid rgba(0, 0, 0, 0.2) !important;
        border-bottom: 1px solid rgba(255, 255, 255, 0.75) !important;
    }
    #mw-navigation #p-lang {
        margin-top: -15px !important;
    }
    #mw-navigation #p-lang .uls-settings-trigger {
        margin-right: 8px !important;
        margin-top: 6px !important;
    }

    /*[[logo]]*/

/*
BODY
*/
    
    p {
        font-size: /*[[fontsize]]*/px;
    }
    .mw-editsection {
        float: right !important;
    }
    body {
        border: none !important;
    }
    .infobox {
        border: 0px solid #aaaaaa !important;
        background-color: #f6f6f6;
        padding: 0.6em !important;
    }
    .Top_icon_raw#tpl_Coordinaten {
        right: 0px !important;
        top: -3px !important;
    }
    #tpl_Coordinaten > span {
        font-size: 78% !important;
    }
    #tpl_Coordinaten > span > img {
        width: 18px !important;
        height: auto !important;
    }
    #content {
        margin: 0 auto !important;
        margin-left: 40px !important;
        border: none !important;
    }
    /*[[headings]]*/
    
    #toc {
        padding: 1px 16px 5px !important;
    }
    #toc #toctitle {
        margin-top: 7px !important;
        text-align: left !important;
        padding-bottom: 0 !important;
    }
    #bodyContent .infobox {
        margin-left: 30px !important;
    }
    #bodyContent #siteSub,
    #bodyContent #mw-fr-revisiontag,
    #bodyContent #tfd.boilerplate {
        display: none !important;
    }
    #bodyContent #coordinates {
        padding: 0px 6px 3px 0px !important;
        border: 0px !important;
    }
    html[lang="de"] #bodyContent #coordinates {
        top: 53px !important;
        right: 32px !important;
    }
    #bodyContent #protected-icon,
    #bodyContent #good-star,
    #bodyContent #spoken-icon,
    #bodyContent #featured-star {
        top: -45px !important;
    }
    p#wikifybarrr {
        margin-bottom: 20px !important;
    }
    #bodyContent #contentSub {
        margin: 20px 0 12px -5px !important;
        padding: 0 !important;
    }
    #bodyContent table.ambox,
    #bodyContent #disambigbox {
        margin: 0 !important;
        font-size: 14px !important;
        line-height: 22px !important;
    }
    #bodyContent #disambigbox {
        margin: 25px 0 !important;
    }
    #bodyContent .references {
        color: #111 !important;
        font-size: 10px !important;
    }
    .mediaContainer {
        z-index: 0;
    }
    span#coordinates.metadata.coord-missing {
        display: none !important;
    }
    #coordinates {
        margin: -20px 0 !important;
    }

/*
FOOTER
*/
    
    #footer {
        left: 0 !important;
        right: 0 !important;
        margin: 0 !important;
        margin-bottom: -22px !important;
        padding: 15px 0 !important;
        background-color: #e2e0de !important;
    }
    #footer-info {
        max-width: 860px !important;
        margin: 0 auto !important;
        font-size: 18px !important;
    }
    #footer li:not(#footer-info-lastmod) {
        display: none !important;
    }
}
