css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("forum.projanmo.com") {
    body { -webkit-font-smoothing: antialiased !important; 
        text-shadow: 1px 1px 1px rgba(0,0,0,0.08) !important; 
    }
    .brd-page { font: 65% "/*[[font]]*/", SolaimanLipi, AponaLohit, Verdana, Helvetica, Arial, sans-serif !important;
        letter-spacing: -.5px !important;
    } * {font-size: /*[[fsize]]*/;}
    .brd samp, .brd code, .brd pre, .brd option, .brd optgroup, .brd input, .brd select, .brd textarea, .brd td, .brd th {
        font-family:  "/*[[font]]*/", SolaimanLipi,AponaLohit,Verdana,Helvetica,Arial,sans-serif !important;
    }
    span {
        letter-spacing: 0px;
    }
    samp, code, pre, option, optgroup, input, select, textarea, td, th {
        font-family:  "/*[[font]]*/", SolaimanLipi,AponaLohit,Verdana,Helvetica,Arial,sans-serif !important;
    }
}
