@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("lolnexus.com") {
    
    .ad-container {
        /*[[ad_display]]*/
    }
    
    .lolchat-upsell {
        /*[[ad_display]]*/
    }
    
    
    /*[[ad_cv_display]]*/
    
    
    .p-header-a {
        background-color: #1a1a1a !important;
    }
    
    body.body-gameinfo {
        background: #222 !important;
    }
    
    #site-main {
        background: #222 !important;
    }
    
    #content, .single-column #content {
        background: #222 !important;
    }
    
    .pro-build .build-list .pro-build-details .header {
        background-color: #333 !important;
    }
    
    .pro-build .build-list .pro-build-details .header .player .player-name {
        color: #ccc !important;
    }
    
    .content-container h1, .content-container h2, .content-container h3, .content-container h4, .content-container h5, .content-container h6 {
        color: #aaa !important;
    }
    
    .header-bar h2 small {
        color: #ccc !important;
    }
    
    .noxia-search table tbody tr:nth-child(2n+1) td {
        background: #373737 !important;
        color: #ccc !important;
    }
    
    .noxia-search table tbody tr:nth-child(2n) td {
        background: #444 !important;
        color: #ccc !important;
    }
    
    .noxia-search table tbody tr td.name a {
        color: #ccc !important;
    }
    
}
