css:
@-moz-document domain("naruho.do") {


  body {
    line-height: inherit !important;
    font-family: /*[[font-family]]*/ !important;
  }

  /* Profile avatar */
  #side img.avatar,
  #settings .profile_pic img {
    max-width: 200px;
    width: auto;
  }

  /* Friend list avatars */
  .friends ul {
    text-align: center;
  }

  .friends img {
    width: auto;
    height: auto;
    max-width: 48px;
    max-height: 48px;
  }

  /* Feed owner avatar */
  .avatar {
    line-height: 0;
  }

  .avatar.rounded,
  .avatar.rounded img,
  #fullnotification div.pic,
  #fullnotification div.pic img {
    height: auto;
    width: auto;
    max-width: 64px;
    border-width: 2px;
  }

  .avatar.rounded {
    border-radius: 0;
  }

  .avatar.rounded img,
  .discussion-post .avatar,
  #fullnotification div.pic,
  #navBar #user_actions .username .profile {
    border-radius: /*[[avt-roundness]]*/;
  }

  .avatar.rounded img {
    max-height: 146px;
  }

  /* Feed and forum comments avatars */
  .avatar,
  #long_feeds .avatar,
  #side img.avatar {
    border: none !important;
    background-color: transparent !important;
  }

  .comments .avatar,
  .comments .avatar img,
  .discussion-post .avatar,
  .discussion-post .avatar img {
    height: auto;
  }

  .discussion-post .avatar {
    width: auto !important;
  }

  .discussion-post .avatar img {
    width: auto !important;
    max-height: 108px !important;
  }

  .discussion .discussion-item-author {
    margin: -6px 0 0 -80px;
  }

  .discussion-item-author img {
    max-width: 72px;
  }

  /* Notification avatars */
  #navBar #user_actions li.items .icon .ua-notifications .avatar {
    width: auto;
    height: auto;
    max-width: 40px;
  }

  /* Forum avatars */
  .discussion-item-author img {
    height: auto;
  }

  /* Feed images */
  .message div.center {
    margin-right: 5px;
    height: auto !important;
  }

  #long_feeds .comments .message div.center img,
  .message div.center img {
    margin-top: 0 !important;
    width: auto !important;
    max-width: 100%;
    height: auto;
    /*[[image_limitHeight]]*/
  }

  /* Friend grid */
  #friends_block .gridded_list li,
  #userSocial_block  .gridded_list li {
    width: 19%;
    text-align: center;
    margin: 0 .5% 25px;
  }

  #friends_block .gridded_list li img,
  #userSocial_block  .gridded_list li img {
    max-width: 100%;
    max-height: 100%;
    width: auto;
    height: auto;
  }

  #friends_block .gridded_list li:nth-child(5n+1),
  #userSocial_block  .gridded_list li:nth-child(5n+1) {
    clear: left;
  }

  /*[[small_friend_grid]]*/
  /* Wallpaper */
  #wrap_content {
    background-image: url('/*[[wall_image]]*/') !important;
    background-position: center;
    background-attachment: /*[[wall_attach]]*/;
    background-size: /*[[wall_stretch]]*/;
  }

  /* Hide Adds */
  .pub_300,
  .adsbygoogle {
    display: none !important;
  }

  /* Misc */
  #social a {
    border-radius: 17px;
  }

  #settings .upload input[type="file"] {
    height: 100%;
    padding-left: 18px;
  }

  #settings .banner_image,
  #settings .banner_image span {
    height: auto;
  }

  #settings .profile_pic,
  #settings .banner_image {
    min-height: 42px;
  }

  #long_feeds .comments {
    margin: 0 !important;
    border-top: none !important;
    border-bottom: none !important;
  }

  #long_feeds .message {
    margin-bottom: 0;
  }

  #long_feeds .message .social_opt {
    margin: 0 0 -1px;
  }

  #settings .profile_pic,
  #settings .profile_pic .upload {
    width: 610px;
  }

  #side {
    height: auto;
  }

  .comment_wrapper .comments {
    clear: none;
  }

  .comment_wrapper .add-comment {
    border-top: 0;
  }

  .comment_wrapper .add-comment .buttons input {
    height: 32px !important;
    width: auto !important;
  }

  .comments .delete {
    margin-top: -2px !important;
  }

  .more-comments {
    width: 102.1%;
    box-sizing: border-box;
  }

  [id^="ndo-message"] .message {
    border-bottom: 0;
  }

  #navBar #user_actions .username {
    text-align: center;
  }

  #navBar #user_actions .username div {
    text-align: left;
  }

  #navBar #user_actions .username .profile {
    width: auto;
  }

  #navBar .icon {
    top: -8px !important;
    height: 35px !important;
    border-radius: 3px !important;
    width: 52px !important;
    margin: 0 1px;
    cursor: default;
  }

  #navBar .icon>img {
    top: 4px !important;
    cursor: pointer;
  }

  #navBar #notification:not(.new):hover {
    background-color: #d9a52b;
  }

  div.actions a.delete {
    display: block !important;
  }

  /*[id^="ndo-comment"] div.actions a.delete, */
  #shareForm .post.selected[href="#"] {
    display: none !important;
  }

  img[src="/images/h-loading.gif"],
  img[src="https://cdn.naruho.do/images/h-loading.gif"] {
    margin: auto;
    display: block;
  }

  textarea {
    resize: vertical !important;
  }

  /* Forum */
  #composer-box {
    max-height: 100vh;
    overflow: auto;
  }

  @media screen and (max-width:1040px) {

    div.content {
      margin: 0;
    }

    #board #long_feeds {
      width: 78% !important;
    }

    #board #long_feeds .message {
      width: 87% !important;
    }

  }

  /* 1920px */
  @media (min-width:1900px) {

    body {
      font-size: 15px !important;
    }

    .comments,
    .add-comment textarea {
      line-height: 17px !important;
    }

    .date, .date a {
      font-size: 13px !important;
    }

    #wrap_content > .content {
      max-width: 1280px !important;
    }

    #navBar {
      width: 68% !important;
      max-width: none;
      box-sizing: border-box !important;
    }

    #shareForm {
      width: 82% !important;
      padding-left: 1px !important;
      box-sizing: border-box !important;
    }

    #shareForm {
      margin: 0 auto 24px !important;
    }

    #shareForm #typeshare {
      box-sizing: border-box;
      margin-top: -1px;
    }

    #shareForm #sharebox,
    #shareForm #typeshare,
    #shareForm #picture_prev {
      width: 100% !important;
    }

    #long_feeds {
      width: 82.5%;
    }

    #long_feeds .message,
    #long_feeds .comments,
    #long_feeds .add-comment,
    #long_feeds .load-more {
      width: 92% !important;
    }

    #board #long_feeds {
      width: 100%;
    }

    #board #long_feeds .message,
    #board #long_feeds .comments,
    #board #long_feeds .add-comment {
      width: 89.5% !important;
    }

    #board #long_feeds [id*="ndo-message-"] {
      width: 100%;
      padding-right: 9%;
      box-sizing: border-box;
    }

    #board #long_feeds .avatar.rounded {
      width: 10%;
      max-width: 10%;
      box-sizing: border-box;
      padding-right: 12px;
      text-align: right;
    }

    #board #long_feeds .avatar.rounded img {
      max-width: 100%;
      box-sizing: border-box;
    }

    #board #long_feeds .comments .message {
      width: 93.6% !important;
    }

    #board #long_feeds .load-more {
      margin: auto;
      width: 80% !important;
      float: none;
      box-sizing: border-box;
    }

    #long_feeds .comments .message {
      width: 93.15% !important;
    }

    #long_feeds .more-comments {
      width: 100%;
      margin: 0;
    }

    #long_feeds.particular {
      margin: 0 !important;
    }

    #shareForm #sharebox {
      min-height: 60px !important;
    }

    #feeds-rightBlock {
      position: absolute;
      top: 0;
      right: calc(80px - (50vw - 576px - 17px));
      width: calc(50vw - 576px - 17px);
    }

    #feeds-rightBlock > * {
      width: 100% !important;
    }

    #feeds-rightBlock .hashtag a:last-child {
      margin-bottom: 0;
    }

    [id*="ndo-comment-"] textarea {
      min-height: 24px !important;
    }

    .mt_video:not(.smallPlayer) {
      height: 578px;
    }

  }



  /*[[avatar-shadows]]*/

  /*[[hide_new_topics]]*/

  /*[[hide_trending]]*/

  /*[[hide_new_releases]]*/

  /*[[truncate_sidebar]]*/

  /*[[scroll_header]]*/

  /*[[smallerListRows]]*/

  /*[[feedHeightLimit]]*/

  /*[[bigger_rating]]*/

  /*[[hide_vote_icons]]*/

  /*[[better_list_update]]*/

  /*[[compact]]*/


}
