css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document regexp('http://imgur\\.com/gallery/.*'), regexp('http://imgur\\.com/(r|a|account/favorites)/.*./.*'), regexp('http://imgur\\.com/(user/.*.|account)/favorites/.*') { 
  .social {
    display: none;
  }
  #topbar, #cta-container-placeholder {
   max-width: calc(100% - 314px);
  }
  .header-center {
    width: auto;
    min-width: 1000px;
  }
  a.zoom {
    width: 500px;
  }
  .advertisement {
    display: none;
  }
  .left {
    margin-left: 12px;
  }
  #right-content {
    position: fixed;
    height: calc(100% - 95px);
    right: 0;
    top: 0;
  }
  #side-gallery {
    height: calc(100%);
    display: block;
    background: rgb(24, 24, 23);
  }
  #side-gallery .content {
    height: 100%;
  }
  #fixed-side-footer-links {
    display: none;
  }
  #side-gallery .nano {
    height: 100%;
    position: absolute;
    top: 60px;
  }
  .layout-options {
    position: absolute;
    top: calc(100% + 60px);
    width: inherit;
  }
  .post-title {
    max-width: none;
  }
  .post-header {
    width: calc(100% - 40px);
  }
  .post-header.fixed {
    width: calc(100% - 366px);
  }
  .post-pad {
    width: calc(100% - 326px);
  }
  .post-container {
    width: 100%;
  }
  #inside {
    width: 100%;
    margin: 12px auto;
  }
  .post-image img, .post-image video, .post-image object {
    max-width: 100% !important; 
  }
  .post-image-placeholder {
    width: auto;
    opacity: inherit;
    position: inherit;
  }
  .zoom img:nth-child(2) {
    display:none;
  }
  #comments-container {
    width: auto;
  }
}

@-moz-document domain("imgur.com") {
  .outside {
    width: calc(100% - 76px) !important;
    text-align: center;
  }
  .main .panel #imagelist.home-gallery {
    width: calc(100% - 396px);
    float: left;
  }
}
