#ctl00_cphRoblox_PostView1_ctl00_PostTitle {
    margin-bottom: 20px;
    max-width: 882.82px;
    overflow: hidden
}
td {
    max-width: 882.82px;
    overflow: hidden
}
.last-post>.normalTextSmaller,
.thread-link-container {
    max-width: 100px
}
#AdvertisementRight,
#AdvertisingLeaderboard,
#HomeContainer div.left-column>div.left-column-boxes.text,
