css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document url-prefix("http://www.tumblr.com"), url-prefix("https://www.tumblr.com")

{

#home_button
{
padding-right: 10px !important;
}

#home_button a
{
height: 0 !important;
width: 0 !important;
top: -5px !important;
padding-left: 25px !important;
padding-top: 30px !important;
margin-top: 5px !important;
background: url('/*[[theimage]]*/') no-repeat !important;
background-size: 23px 25px !important;
overflow: hidden;
}

#home_button .tab_notice
{
left: 26px !important;
right: auto !important;
}
  
.l-header .selection_nipple
{
top: 8px !important;
}
}

@-moz-document url-prefix("http://www.tumblr.com/inbox"), url-prefix("https://www.tumblr.com/inbox")

{
  
.l-header .selection_nipple
{
top: 5px !important;
}
}

@-moz-document url-prefix("http://www.tumblr.com/help"), url-prefix("https://www.tumblr.com/help")

{
  
.l-header .selection_nipple
{
top: 5px !important;
}
}

@-moz-document url-prefix("http://www.tumblr.com/settings"), url-prefix("https://www.tumblr.com/settings")

{
  
.l-header .selection_nipple
{
top: 5px !important;
}
}
