css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("jsbin.com") {
  .CodeMirror-code {background: black !important}
  #bin .editbox .CodeMirror {background:black}
  .label.menu span strong a {color:white;text-shadow:none}
  .label.open .name > strong {background:#333;border-color:#333}
  .dropdownmenu, .dropdowncontent {background:#333;border-color:#333}
  .dropdownmenu a:hover, .button:hover, a.hover {background:#444}
  .dropdownmenu a, .button, a {text-shadow:none;color:white}
  .dropdownmenu .small {color:#ccc}
  .buttons {background:#333}
  #control, .control {background:transparent !important}
  .open .button-dropdown, .open .button-dropdown:hover, .open .button-open, .open .button-open:hover {background:#444;border-color:#444}
  a[data-pro]:after, .pro-required {background:#555;border-color:#555}
  hr {border-color:#555}
  .button.group.hasContent, .button.group.active {background:#555 !important}
  .dropdownmenu a:hover, .button:hover, a.hover {background:#444;border-color:#444}
  .panel:before {background:#555}
  .label {background:#000;color:#fff;line-height:26px}
  .label .name {color:#fff}
  .resize {border-color:#333 !important}
  .card header {background:#444;color:#fff;height:28px}
  .card {border-color:#444}
  
  /*log*/
  .console-log-head {color:#fff;background:#444;border-color:#444}
  .console-log {background:#333;color:#fff}

  /*menu*/
  #toppanel {background:#555 !important;color:#fff !important}
  .toppanel-wrapper a {color:#ccc !important}
  #enableUniversalEditorLabel {color:#ccc !important}
  .toppanel-wrapper .toppanel-no-pro a {background:#666 !important;color:#fff !important}
  .toppanel-love {border-color:#666 !important}
  .toppanel-button {border-color:#666 !important}
  
  .CodeMirror-code pre {color:white}
  .cm-s-jsbin span.cm-tag  {color:/*[[cm-tag]]*/ !important}
  .cm-s-jsbin span.cm-property {color:/*[[cm-property]]*/ !important}
  .cm-s-jsbin span.cm-keyword {color:/*[[cm-keyword]]*/ !important}
  .cm-s-jsbin span.cm-builtin {color:/*[[cm-builtin]]*/ !important}
  .cm-s-jsbin span.cm-variable {color:/*[[cm-variable]]*/ !important}
  .cm-s-jsbin span.cm-number {color:/*[[cm-number]]*/ !important}
  .cm-s-jsbin span.cm-atom {color:/*[[cm-atom]]*/ !important}
  .cm-s-jsbin span.cm-def {color:/*[[cm-tag]]*/ !important}
  .cm-s-jsbin span.cm-variable-3 {color:/*[[cm-variable3]]*/ !important}
  .cm-s-jsbin span.cm-operator {color:/*[[cm-operator]]*/ !important}
  .cm-s-jsbin span.cm-variable-2 {color:/*[[cm-variable2]]*/ !important}
  .cm-s-jsbin span.cm-attribute {color:/*[[cm-attribute]]*/ !important}
  .cm-s-jsbin span.cm-meta {color:/*[[cm-meta]]*/ !important}
  .cm-s-jsbin span.cm-comment {color:/*[[cm-comment]]*/ !important}
  .CodeMirror-activeline-background.CodeMirror-linebackground {background:/*[[cm-active]]*/ !important}
  .CodeMirror div.CodeMirror-cursor {border-color:/*[[cm-cursor]]*/}
  ::selection {background:#333 !important}
  ::-moz-selection {background:#333 !important}
}
