css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document url("http://www.google.com/"),
	regexp("https?:\\/\\/(www|encrypted)\\.google\\..*?\\/($|(search|webhp|)\\?.*)"){



/*********** Search Results ******************/

/* 横幅修正 */
#rcnt > DIV[style="width:0"][class="col"]{
    width:100%!important;
}
#desktop-search TD[valign="top"]:nth-child(2){
    width:100%!important;
}
#mn{
    table-layout:auto;
}

/* Max-width [search results] */
#cnt > DIV[class="mw"],
#center_col{
    max-width: none !important;
    width:90%!important;
}

#rso .s{
    font-size:12px;
    margin:0!important;
    max-width: none !important;
    width:90% !important; 
}
.st{
       word-wrap: normal!important;
}

/* Result margin */
Li.g{margin-bottom:5px !important;}


/* background */
#gsr{
    background: url(/*[[imageurl]]*/)  no-repeat fixed right bottom !important;
    background-size:22.45% auto!important;
}
.mnr-c{
    background:none!important;
    box-shadow: none!important;
}

/* 右に出るwikiなどの解説 */
#rhscol,
#rhs_block   /* スクリプト無効時の右側セル */
{
    display:none!important;
}


/*セーフサーチ*/
#ab_ctl_ss{
    display:none;
}



}/* end */
