css:
@-moz-document domain("habitrpg.com") {
  .box-shadowed {
    -webkit-box-shadow: 0 1px 1px rgba(90, 90, 90, 0.15)!important;
    box-shadow: 0 1px 1px rgba(90, 90, 90, 0.15)!important;
  }
  body {
    background: #eff3f4!important;
  }
  /*-----------------------------------------------------------------------------------*/
  /* Columns & Task */
  /*-----------------------------------------------------------------------------------*/
  .container-fluid.tasks-lists {
    padding: 15px!important;
  }
  .container-fluid.tasks-lists .row {
    width: 100%!important;
    margin: 0!important;
  }
  .container-fluid.tasks-lists .row .col-md-3 {
    padding: 0!important;
  }
  .container-fluid.tasks-lists .row .col-md-3:last-child .task-column {
    margin: 0!important;
  }
  .container-fluid.tasks-lists .row .col-md-3 div.task-column {
    padding: 10px!important;
    margin-right: 10px!important;
    background: #ffffff!important;
    border: 1px solid #dee5e7!important;
    border-radius: 4px!important;
    -webkit-box-shadow: 0 1px 1px rgba(0, 0, 0, 0.05)!important;
    box-shadow: 0 1px 1px rgba(0, 0, 0, 0.05)!important;
  }
  .container-fluid.tasks-lists .row .col-md-3 div.task-column .task-column_title {
    color: #444444!important;
    font-size: 18px!important;
    font-weight: 600!important;
    padding: 10px 0!important;
  }
  .container-fluid.tasks-lists .row .col-md-3 li.task {
    margin-bottom: .5em!important;
    border-radius: 2px!important;
    border-bottom-color: #D8E1E3!important;
    -webkit-box-shadow: 0 1px 1px rgba(90, 90, 90, 0.15)!important;
    box-shadow: 0 1px 1px rgba(90, 90, 90, 0.15)!important;
  }
  .container-fluid.tasks-lists .row .col-md-3 li.task .task-text {
    margin-left: 2.5em!important;
    padding: .75em .5em!important;
    line-height: 1.6!important;
  }
  .container-fluid.tasks-lists .row .col-md-3 li.task .task-text img.markdown-img {
    padding-right: 1em!important;
  }
  .container-fluid.tasks-lists .row .col-md-3 li.task:last-of-type {
    margin: 0!important;
  }
  .container-fluid.tasks-lists .row .col-md-3 li.task.reward-item .task-text,
  .container-fluid.tasks-lists .row .col-md-3 li.task.reward .task-text {
    margin-left: 5.5em!important;
  }
  .container-fluid.tasks-lists .row .col-md-3 li.habit .task-text {
    margin-left: 3.5em!important;
  }
  .container-fluid.tasks-lists .row .col-md-3 li.habit-wide.habit .task-text {
    margin-left: 6.5em!important;
  }
  .task-action-btn[ng-if="task.up"],
  .task-action-btn[ng-if="task.down"] {
    height: 100%!important;
    width: 2em!important;
    line-height: 46px!important;
    font-size: 1.5em!important;
  }
  .task-action-btn.tile {
    color: #58666E !important;
    border: 1px solid #DEE5E7!important;
    background-color: #FFF!important;
    border-bottom-color: #D8E1E3!important;
    padding: 6px 12px!important;
    font-size: 14px!important;
    font-weight: normal!important;
    line-height: 1.4em!important;
    border-radius: 2px!important;
    outline: none!important;
    -webkit-box-shadow: 0 1px 1px rgba(90, 90, 90, 0.15)!important;
    box-shadow: 0 1px 1px rgba(90, 90, 90, 0.15)!important;
  }
  .repeat-days .task-action-btn.tile {
    padding: 0 .5em!important;
  }
  .checkbox input[type=checkbox] {
    height: 22px!important;
    margin: 0!important;
    margin-left: -20px!important;
  }
  [class$="-options"]:not(.toolbar-options) {
    padding: 1em!important;
  }
  .alert:not(.ui-pnotify-container) {
    margin: 20px 0!important;
    padding: 10px 5px!important;
  }
  ul.todos.main-list {
    padding-top: 55px!important;
  }
  .ng-scope.tabbable.tabs-below .nav-tabs {
    position: absolute!important;
    top: 110px!important;
    border-bottom-color: #dee5e7!important;
    width: 90%!important;
  }
  .ng-scope.tabbable.tabs-below .nav-tabs li.active > a {
    border-color: #dee5e7!important;
    border-bottom-color: rgba(0, 0, 0, 0)!important;
  }
  .ng-scope.tabbable.tabs-below .nav-tabs li > a {
    border-radius: 2px 2px 0 0!important;
  }
  .ng-scope.tabbable.tabs-below .nav-tabs li > a:hover,
  .ng-scope.tabbable.tabs-below .nav-tabs li > a:focus {
    background-color: #edf1f2!important;
  }
  ul.todos.main-list.ng-hide + br + div + .tabs-below .nav-tabs{
    position: relative!important;
    top: 0!important;
  }
  form.addtask-form.ng-hide {
    display: block!important;
  }
  /*-----------------------------------------------------------------------------------*/
  /* Checker */
  /*-----------------------------------------------------------------------------------*/
  .task-checker label {
    width: 2em!important;
  }
  .action-yesno label:hover:after,
  .action-yesno label:focus:after,
  .action-yesno input[type=checkbox]:checked + label:after {
    font-size: 2.5em!important;
    line-height: 1!important;
    height: 1em!important;
    width: 1em!important;
  }
  .completed {
    background-color: #edf1f2!important;
    color: #58666E!important;
    border-color: #dee5e7!important;
  }
  .completed .task-checker label {
    background-color: #dee5e7!important;
  }
  .completed .task-options {
    background-color: #edf1f2!important;
  }
  ul.rewards {
    border-bottom: 1px dashed #DEE5E7!important;
  }
  .reward-item .btn-buy,
  .reward .btn-buy {
    background-color: #edf1f2!important;
    padding-left: 0!important;
    width: 5em!important;
  }
  /*-----------------------------------------------------------------------------------*/
  /* Add task */
  /*-----------------------------------------------------------------------------------*/
  .addtask-form {
    outline: none!important;
  }
  .addtask-field input {
    border-radius: 2px!important;
    border: 1px solid #cfdadd!important;
    height: 34px!important;
    padding: 6px 12px!important;
    padding-right: 3.5em !important;
  }
  .addtask-btn {
    height: 100%!important;
    background: none!important;
    font-weight: bolder!important;
    width: 1.5em!important;
    line-height: 0!important;
    font-size: 1.5em!important;
    border: 1px solid #dee5e7!important;
    border-bottom-color: #D8E1E3!important;
    -webkit-box-shadow: 0 1px 1px rgba(90, 90, 90, 0.15)!important;
    box-shadow: 0 1px 1px rgba(90, 90, 90, 0.15)!important;
  }
  .addtask-btn:hover {
    color: #58666E!important;
    background-color: #EDF1F2!important;
    border-color: #C7D3D6!important;
  }
  hr {
    border-bottom: 1px solid #dee5e7!important;
    border-style: dashed!important;
  }
  /*-----------------------------------------------------------------------------------*/
  /* Hide Unused Things */
  /*-----------------------------------------------------------------------------------*/
  div.filters.ng-scope {
    display: none!important;
  }
  .glyphicon-open,
  .task-meta-controls a.badge,
  .task-meta-controls .glyphicon-tags,
  span.task-notes,
  .toolbar-toggle {
    display: none!important;
  }
  /*-----------------------------------------------------------------------------------*/
  /* Header */
  /*-----------------------------------------------------------------------------------*/
  .herobox.noBackgroundImage,
  .herobox.noBackgroundImage.isUser {
    background: none!important;
  }
  header .hero-stats {
    background: none!important;
    border: none!important;
  }
  .header-wrap {
    border: none!important;
    background: #E4EAEC url(/*[[h-img]]*/) top center!important;
    /*[[bg-options]]*/
    padding-top: 50px!important;
    -webkit-box-shadow: 0 2px 2px rgba(0, 0, 0, 0.1), 0 1px 0 rgba(0, 0, 0, 0.1)!important;
    box-shadow: 0 2px 2px rgba(0, 0, 0, 0.1), 0 1px 0 rgba(0, 0, 0, 0.1)!important;
  }
  nav.toolbar {
    border: none!important;
    background: #1C2B36 url("http://lx2.com.my/habitrpg/habitrpg-icon.png") top left no-repeat!important;
    padding-left: 10em!important;
  }
  nav.toolbar:before {
    content: "HabitRPG"!important;
    color: #FFF!important;
    font-weight: bold!important;
    padding: .75em 1.5em!important;
    position: absolute!important;
    left: 1.5em!important;
  }
  /*-----------------------------------------------------------------------------------*/
  /* Toolbar */
  /*-----------------------------------------------------------------------------------*/
  /* npc_bailey */
  .npc_bailey.npc_bailey_head {
    height: 72px!important;
  }
  .toolbar-bailey-container {
    position: absolute!important;
    top: 150px!important;
    right: 5em!important;
  }
  /* Wallet */
  .toolbar-wallet {
    padding: .5em!important;
  }
  .toolbar-wallet span {
    color: #fff!important;
    font-weight: bold!important;
  }
  /* Menu */
  .toolbar-nav {
    padding: 0!important;
  }
  nav.toolbar .toolbar-nav .toolbar-button-dropdown,
  nav.toolbar .toolbar-nav .toolbar-button,
  nav.toolbar .toolbar-options li,
  nav.toolbar .toolbar-subscribe .toolbar-subscribe-button {
    border: none!important;
    border-radius: 0!important;
    margin-right: 1.5em!important;
    padding: 0.5em!important;
  }
  nav.toolbar .toolbar-nav .toolbar-button-dropdown > a,
  nav.toolbar .toolbar-nav .toolbar-button > a,
  nav.toolbar .toolbar-options li > a,
  nav.toolbar .toolbar-subscribe .toolbar-subscribe-button > a,
  nav.toolbar .toolbar-nav .toolbar-button-dropdown > button,
  nav.toolbar .toolbar-nav .toolbar-button > button,
  nav.toolbar .toolbar-options li > button,
  nav.toolbar .toolbar-subscribe .toolbar-subscribe-button > button,
  nav.toolbar .toolbar-nav .toolbar-button-dropdown > input,
  nav.toolbar .toolbar-nav .toolbar-button > input,
  nav.toolbar .toolbar-options li > input,
  nav.toolbar .toolbar-subscribe .toolbar-subscribe-button > input,
  nav.toolbar .toolbar-nav .toolbar-button-dropdown > a span,
  nav.toolbar .toolbar-nav .toolbar-button > a span,
  nav.toolbar .toolbar-options li > a span,
  nav.toolbar .toolbar-subscribe .toolbar-subscribe-button > a span,
  nav.toolbar .toolbar-nav .toolbar-button-dropdown > a:nth-of-type(2),
  nav.toolbar .toolbar-nav .toolbar-button > a:nth-of-type(2),
  nav.toolbar .toolbar-options li > a:nth-of-type(2),
  nav.toolbar .toolbar-subscribe .toolbar-subscribe-button > a:nth-of-type(2) {
    color: #7CA1BB!important;
    border: none!important;
    background: none!important;
    font-weight: bold!important;
    font-size: 14px!important;
    border-radius: 0!important;
  }
  nav.toolbar .toolbar-nav .toolbar-button-dropdown > a:hover,
  nav.toolbar .toolbar-nav .toolbar-button > a:hover,
  nav.toolbar .toolbar-options li > a:hover,
  nav.toolbar .toolbar-subscribe .toolbar-subscribe-button > a:hover,
  nav.toolbar .toolbar-nav .toolbar-button-dropdown > button:hover,
  nav.toolbar .toolbar-nav .toolbar-button > button:hover,
  nav.toolbar .toolbar-options li > button:hover,
  nav.toolbar .toolbar-subscribe .toolbar-subscribe-button > button:hover,
  nav.toolbar .toolbar-nav .toolbar-button-dropdown > input:hover,
  nav.toolbar .toolbar-nav .toolbar-button > input:hover,
  nav.toolbar .toolbar-options li > input:hover,
  nav.toolbar .toolbar-subscribe .toolbar-subscribe-button > input:hover,
  nav.toolbar .toolbar-nav .toolbar-button-dropdown > a span:hover,
  nav.toolbar .toolbar-nav .toolbar-button > a span:hover,
  nav.toolbar .toolbar-options li > a span:hover,
  nav.toolbar .toolbar-subscribe .toolbar-subscribe-button > a span:hover,
  nav.toolbar .toolbar-nav .toolbar-button-dropdown > a:nth-of-type(2):hover,
  nav.toolbar .toolbar-nav .toolbar-button > a:nth-of-type(2):hover,
  nav.toolbar .toolbar-options li > a:nth-of-type(2):hover,
  nav.toolbar .toolbar-subscribe .toolbar-subscribe-button > a:nth-of-type(2):hover {
    background: none!important;
  }
  nav.toolbar:hover {
    background-color: #16232D!important;
  }
  nav.toolbar:hover > a,
  nav.toolbar:hover > a span,
  nav.toolbar:hover > button {
    color: #fff!important;
  }
  nav.toolbar .toolbar-options li,
  nav.toolbar .toolbar-subscribe-button button,
  nav.toolbar .toolbar-subscribe-button {
    margin-right: 0!important;
  }
  .toolbar-bailey,
  .toolbar-options,
  .toolbar-subscribe {
    padding: 0!important;
  }
  /*-----------------------------------------------------------------------------------*/
  /* Animation */
  /*-----------------------------------------------------------------------------------*/


  .herobox .character-sprites span[class^="weapon_"], .herobox .character-sprites span[class^="shield_"], .herobox .character-sprites span[class^="head_"]:not(.head_0) {
    -webkit-animation: armor-idle 2s linear 2s infinite!important;
    animation: armor-idle 2s linear 2s infinite!important;
  }

  @-moz-keyframes armor-idle {
    0% {

      top: 0px!important;
    }
    50% {

      top: 1px!important;
    }
    100% {

      top: 0px!important;
    }
  }

  @-webkit-keyframes armor-idle {
    0% {

      top: 0px!important;
    }
    50% {

      top: 1px!important;
    }
    100% {

      top: 0px!important;
    }
  }

  @-ms-keyframes armor-idle {
    0% {

      top: 0px!important;
    }
    50% {

      top: 1px!important;
    }
    100% {

      top: 0px!important;
    }
  }

  @-o-keyframes armor-idle {
    0% {

      top: 0px!important;
    }
    50% {

      top: 1px!important;
    }
    100% {

      top: 0px!important;
    }
  }

  @keyframes armor-idle {
    0% {

      top: 0px!important;
    }
    50% {

      top: 1px!important;
    }
    100% {

      top: 0px!important;
    }
  }

  /*-----------------------------------------------------------------------------------*/
  /* Stat Page */
  /*-----------------------------------------------------------------------------------*/
  #main > div > div > div > .tab-content {
    background: #FFF!important;
    border-radius: 2px!important;
  }
  #main ul.options-menu,
  #main ul.options-submenu {
    padding: 1em!important;
  }
  #main ul.options-menu li,
  #main ul.options-submenu li {
    margin: 0!important;
    margin-right: 0.5em!important;
  }
  #main ul.options-menu li.active a,
  #main ul.options-submenu li.active a,
  #main ul.options-menu li.active button,
  #main ul.options-submenu li.active button,
  #main ul.options-menu li > a,
  #main ul.options-submenu li > a,
  #main ul.options-menu li > button,
  #main ul.options-submenu li > button,
  #main ul.options-menu li > input,
  #main ul.options-submenu li > input {
    color: #58666E !important;
    border: 1px solid #DEE5E7!important;
    background-color: #FFF!important;
    border-bottom-color: #D8E1E3!important;
    padding: 6px 12px!important;
    font-size: 14px!important;
    font-weight: normal!important;
    line-height: 1.4em!important;
    border-radius: 2px!important;
    outline: none!important;
    -webkit-box-shadow: 0 1px 1px rgba(90, 90, 90, 0.15)!important;
    box-shadow: 0 1px 1px rgba(90, 90, 90, 0.15)!important;
  }
  #main ul.options-menu li.active a,
  #main ul.options-submenu li.active a,
  #main ul.options-menu li.active button,
  #main ul.options-submenu li.active button {
    color: #58666E!important;
    background-color: #EDF1F2!important;
    border-color: #C7D3D6!important;
    -webkit-box-shadow: inset 0 3px 5px rgba(0, 0, 0, 0.125)!important;
    box-shadow: inset 0 3px 5px rgba(0, 0, 0, 0.125)!important;
  }
  /*[[animated]]*/

ul.todos.main-list{
padding: 0!important;
}

.main-list, ul.rewards{
  margin-top: 75px!important;
}
}
