css:
/*@import url('https://fonts.googleapis.com/css?family=Stalemate');*/
@-moz-document domain("todoist.com") {



/* background colors navigation
*/
body {font-family: /*[[FontFamily]]*/ !important;-webkit-font-smoothing: antialiased;letter-spacing: inherit;} 

#top_filters .filter.current {color:/*[[NavText]]*/!important;}
li.current table .name {font-size:/*[[FontSizes]]*/!important;
-webkit-font-smoothing: antialiased;
}

#top_filters li.current{font-size:/*[[FontSizes]]*/!important; 
-webkit-font-smoothing: antialiased;
}

.inspiration a {
color:#A5A2A2!important;}


#td_logo, .invisible_bg {background-color: /*[[LeftBarColor]]*/ !important;}
.cmp_td_logo_mini {background-color: /*[[LeftBarColor]]*/ !important;}
.left_menu {background: /*[[LeftBarColor]]*/ !important;}




li.clickable.menu_clickable.indent_1.current {
background: /*[[Current]]*/ !important;}
li.clickable.menu_clickable.indent_2.current {
background: /*[[Current]]*/ !important;}
li.clickable.menu_clickable.indent_3.current {
background: /*[[Current]]*/ !important;}


#top_filters li.current{background:/*[[Current]]*/!important;}
li.current table {background-color: /*[[Current]]*/!important;}
li.clickable.menu_clickable.indent_2.current {background:/*[[Current]]*/!important;}

#top_filters .counter.overdue_color {
    color:/*[[Overdue]]*/!important;
    font-weight: bold!important;}

/* improved task counter clarity
*/

.counter  {color: /*[[TaskCounter]]*/ !important; opacity: 0./*[[CounterOpacity]]*/ !important;font-weight:/*[[Fontweight]]*/ !important; font-size:95% !important;}
*.counter_count {color: /*[[TaskCounter]]*/ !important;opacity: 0./*[[CounterOpacity]]*/ !important;font-size:13px!important;}


.task_item, .name, .filter, .control {
    font-weight: /*[[Fontweight]]*/!important;
}


/*[[Brackets]]*/


/* better alignment of icons
*/


/*
.td_color div {border-radius: 12px; height: 10px;width: 10px;margin-top: -2px;margin-left: 6px;}
.td_color div img {display:none;}
*/

/* text colours, font size - nav menu
*/

.name {color: /*[[NavText]]*/ !important; font-size:/*[[FontSizes]]*/!important;}
.filter {color: /*[[NavText]]*/ !important;font-size:/*[[FontSizes]]*/!important;}
.control {color: /*[[NavText]]*/ !important;font-size:/*[[FontSizes]]*/!important;}

.filter:hover, #left_menu .items li:hover {
background-color: /*[[HoverNav]]*/ !important;}



#top_filters li.hover{background:/*[[HoverNav]]*/ !important;}
.filter:hover table,#left_menu .items li:hover table{background-color:/*[[HoverNav]]*/ !important;}





.task_item:hover .content,
.task_item:hover .assign_user,
.task_item:hover .menu,
.task_item:hover .project,
.on_menu_show,
#editor .items li:hover,
#agenda_view .column_project:hover
{
    background-color: /*[[HoverMain]]*/ !important;
}


.items .name{font-size:/*[[FontSizes]]*/!important;}

/* text colours, font size, main text area text
*/

span.text.sel_item_content,body {color: /*[[MainText]]*/ !important;font-size:/*[[FontSizes]]*/;}
div.heading, .section_header a {color: /*[[SectHeader]]*/ !important;font-size:/*[[Fontheader]]*/!important;}
.no_tasks a {color: /*[[SectHeadNone]]*/ !important;font-size:/*[[Fontheader]]*/!important;}


/* main area background colors
*/

#content {background-color: /*[[MainBack]]*/ !important;}
#page_background  {background-color: /*[[Back]]*/ !important;}

/* accent background colors (search box, tick box)
*/

input {background-color: /*[[Accent]]*/;}

.task_item .task_content_item {
border-bottom: 1px solid /*[[Accent]]*/ !important;
}
#left_menu {
    border-right: 1px solid /*[[Vertical]]*/!important;}
#content {
    border-right: 1px solid /*[[Vertical]]*/!important;}

/* Custom logo, with size adjustment, and removal of border if large logo
*/
  
/*[[Logoswitch]]*/


div#search_bar .cmp_search {
width: 13px!important;
padding-left:0px!important;
}

.generic_left_list .arrow {
    margin-left: -20px;
    
}

.generic_left_list .drag_and_drop_handler,#editor .drag_and_drop_handler {
margin-top:2px!important;
}


.AmiMenu .priorities .pri_selected {
    border: 2px solid #848282!important;
    }
.AmiMenu .priorities a {
opacity: 0.8;
}
.ist_checkbox {
    transition: background 100ms ease-in;
    transition-property: background;
    transition-duration: 50ms;
    transition-timing-function: ease-in;
    transition-delay: initial;
}
a.ex_link {
       cursor: pointer !important;
    text-decoration: none;
    padding-left: 22px!important;
    Color:#65B6EC!important;}


.ex_link, .task_item a:before {
   background: url("https://image.ibb.co/hhM64F/netlink3.png") 0 0 no-repeat;
    background-position: 0 -191px!important;
  }

.task_item span a {
    color: #65B6EC;}


#agenda_view .agenda_item__time {
    padding-right:5px!important;
    color: /*[[TimeStamp]]*/ !important;
}

#agenda_view .agenda_item__time_overdue {
    padding-right:5px!important;
    color: /*[[TimeStampOver]]*/ !important;
}




div#amicomplete_floater {
    min-height: 500px;
}

.AmiMenu .priorities a {
    text-align: center;
    margin: 5px 4px 4px 9px;
}

.subsection_header {
       padding-bottom: 1em !important;
    margin-bottom: 10px !important;
border-bottom-color: #858b8f!important;}


#editor .items .indent_2 {
    padding-left: 28px !important;
}


.cmp_open_arrow_down {
    margin-left: 3px!important;
margin-bottom:1px!important;
}


.cmp_open_arrow_right {
    margin-left: 3px!important;
margin-bottom:1px!important;
}

#left_menu .cmp_open_arrow_right {
    margin-left: 3px!important;
margin-bottom:10px!important;
}

#left_menu .cmp_open_arrow_down {
    margin-left: 3px!important;
margin-bottom:10px!important;
}

#editor {
    padding-top: 80px!important;
}

.task_item a:hover {
    text-decoration: none!important;
}



#agenda_view .subsection_header.warning {
     padding-bottom: 30px!important;
}
#completed_today .heading {
    margin-top: 16px;
    font-size: 16px;
    color: #db4c3f !important;
}

.task_item td.checker, .task_item .task_content_item {

    border-bottom: 0px solid #f0f0f0!important;
}


.text > code {
    background-color: #3d4951;
    color: #d3d9de;
}


#editor .items li.manager.indent_1 {
    padding-left: 34px!important;
}
#editor .items li.manager.indent_2 {
    padding-left: 62px!important;
}
#editor .items li.manager.indent_3 {
    padding-left: 90px!important;
}
#editor .items li.manager.indent_4 {
    padding-left: 118px!important;
}
#editor .items li.manager.indent_5 {
    padding-left: 146px!important;
}

#editor .items li .text {
    font-size: /*[[Fonttext]]*/em;
   }

.form_icon_holder span, .form_icon_holder img {
    margin-right: 1px!important;
}

.form_icon_holder span {
    margin-left: 1px;
    padding-bottom: 3px !important;
    display: inline-block !important;
}

div#todoist_holder {
    min-height: 800px!important;
}
iframe#todoist_iframe {
    min-height: 800px!important;
}

/*#logo .light_icon path {
    fill: /*[[Logocol]]*/!important;
}
*/
#top_bar #logo svg {
    color:/*[[Logocol]]*/!important;
}

