div.post.reply.hidden.post-hover, div.post.reply.post-hover {
  margin-left: initial !important;
  max-width: 45%;
  min-width: 400px;
  width: 45%;
}  
.reply div.post.reply.post-hover {
  width: 95% !important;
  max-width: 95%!important;
  display:  table !important;
  position: relative !important;
  top: 0 !important;
  left: 0 !important;
  margin: 10px auto !important;
  box-shadow: none !important;
  
}
  
hr {
  height: 1px;
  border: none;
  box-shadow: 0 1px 1px rgba(240,113,134,0.1)!important;
  margin: 30px !important;
}
span.unimportant {
  font-size: 0px !important;
}
span.b_group {
  margin-right: -11px;
}
p.fileinfo {
  max-width:20%;
  text-overflow: ellipsis;
  white-space: nowrap;
  overflow: hidden;
}

p.fileinfo a:first-child {
  display: none;
}

.post.reply p.fileinfo,
.post.reply .postfilename,
.post.reply .unimportant,
.post.reply .btn_bu 
{
  
  max-width: 100% !important;
  font-size: 10px!important;
   
}  
.postfilename,.btn_bu,p.fileinfo {
  font-size: 14px !important;
}
