css:
@-moz-document regexp("(http|https):\\/\\/.*\\.kaskus\\.co\\.id(\\/.*)?") {
body.q ul.nav-header>li#kk-forum.current a {
background: /*[[base]]*/;
}

#forum-listing .listing-table thead {
background: /*[[base]]*/;
}

.blue.button {
background-color: /*[[base]]*/;
transition: background 0.2s;
}

.blue.button:focus, .blue.button:hover {
background: /*[[hover]]*/;
transition: background 0.2s;
}

body.forum.inner.q .pagination .active a{
  background-color: /*[[hover]]*/!important;
}

body.forum.q .toolbar a:hover, body.forum.q .toolbar button:hover{
  background: /*[[base]]*/ !important;
}

.hfeed .entry-head {
background-color: /*[[base]]*/;
}

.kasad-frame{
  display:none;
}
  
.kasad-h{
  display:none;
}

.st-btn#c-thread {
  background: /*[[base]]*/; 
}

.st-btn#c-thread:hover{
  background: /*[[hover]]*/; 
}

ul.tab-nav > li.active > a, ul.tab-nav > li.active > a:hover, ul.tab-nav > li.active > a:focus {
color: #fff;
background-color: /*[[base]]*/;
border: 1px solid /*[[base]]*/;
border-bottom-color: transparent;
cursor: default;
}
    
ul.tab-nav > li.active > a:after{
border-color: /*[[base]]*/ transparent;
}
    
ul.tab-nav > li > a {
border: 1px solid /*[[base]]*/;
color: /*[[base]]*/;
transition: all 0.2s ease-in-out;
}

ul.tab-nav > li > a:hover{
  background: /*[[hover]]*/;
  border-color: /*[[hover]]*/;
  transition: all 0.2s ease-in-out;
}

.searchlike-table .header .search-header{
  background-color: /*[[base]]*/;
}
      
body.forum .refine {
background-color: /*[[base]]*/;
}
      
.filter-forum-cat{
  border-left-color: /*[[base]]*/;
}
  
body.q ul.nav-header > li a {
transition: all 0.2s ease-in-out 0s !important;
}

body.q ul.nav-header > li#kk-forum a:hover{
background: /*[[base]]*/;
transition: all 0.2s ease-in-out;
}

.form-modal-hp li.selected {
color: rgba(247,148,29,1);
border-left: 5px solid /*[[base]]*/;
}

.form-modal-hp label{
  color: /*[[base]]*/;
}

body.forum.q .toolbar li a.active {
background: /*[[base]]*/;
}

.form-modal-hp li:hover, .form-modal-hp li.selected{
border-left-color: /*[[base]]*/;
}
    
ul.user-control #after-login:hover .vcard, ul.user-control #after-login .open .vcard:hover{
border-color: /*[[base]]*/;
}
    
.live-posting{
background: /*[[base]]*/;
transition: all 0.2s ease-in-out;
}
      
.live-posting:hover{
background: /*[[hover]]*/;
transition: all 0.2s ease-in-out;
}
    
.panel-wrapper.menu a.fold{
background: /*[[base]]*/;
transition: all 0.2s ease-in-out;
}
        
.panel-wrapper.menu a.fold:hover{
background: /*[[hover]]*/;
transition: all 0.2s ease-in-out;
}
    
#menu-accordion li.open{
border-left-color: /*[[base]]*/;
}
    
.modal-forum-cat-expand span.intro {
color: /*[[base]]*/;
border-left-color: /*[[base]]*/;
}
      
.modal-forum-cat-expand hr{
border-bottom-color: /*[[base]]*/;
}

.form-modal-forum label{
color: /*[[base]]*/;
transition: all 0.2s ease-in-out;
}
          
.tab-sidebar > li > a{
background: /*[[base]]*/;
transition: all 0.2s ease-in-out;
}
            
.tab-sidebar > li > a:hover{
background: /*[[hover]]*/;
transition: all 0.2s ease-in-out;
}
                
ul#sidebar-category > div.sb-forum li > div#list-category.active-sidebar, ul#sidebar-category > li > div#list-category.active-sidebar, ul#sidebar-subscribe > li > div#list-category.active-sidebar{
border-left-color: /*[[base]]*/;
color: /*[[base]]*/;
transition: all 0.2s ease-in-out;
}
                
ul#sidebar-category > div li > div#list-category, ul#sidebar-category > li > div#list-category, ul#sidebar-subscribe > li > div#list-category{
transition: all 0.2s ease-in-out;
}
                
.tab-qu>li.active>a, .tab-qu>li.active>a:hover, .tab-qu>li.active>a:focus{
background-color: /*[[base]]*/;
border-color: /*[[base]]*/;
}
    
.tab-qu>li.active>a:after{
border-color: /*[[base]]*/ transparent;
}
        
.tab-qu>li>a:hover{
background: /*[[hover]]*/;
border-color: /*[[hover]]*/;
}
    
.user-profile-wrapper .user-tab-nav{
background: /*[[base]]*/;
border-bottom-color: /*[[base]]*/;
}
        
.user-profile-wrapper .user-tab-nav a:focus, .user-profile-wrapper .user-tab-nav a:hover{
transition: all 0.2s ease-in-out;
}
           
.user-profile-wrapper .user-tab-nav a{
transition: all 0.2s ease-in-out;                 
}

.banner-top-ads
{
  display:none;
}

.form-modal-forum li>i.fa{
    color: /*[[base]]*/;
  }

/* Custom Background 26/09/2014 */

/*[[background]]*/

div#main div#main {
background: transparent !important;
}

.l-link{
display:none !important;
  }
.r-link{
display:none !important;
  }

/*[[dark]]*/
}
