css:
@-moz-document domain("8chan.co") {
body {
	background: #383748  repeat;
  	color: #dadada;
	background-image: url(/*[[mascot]]*/), url(http://i.imgur.com/GN1ROrl.png);
  	background-position: 100% 100%, 0% 0%;
    background-attachment: fixed, scroll;
  	background-repeat: no-repeat, repeat;
}

.unimportant {
	color: #dadada;
}

hr {
	border-top: 0px;
}

form table tr th {
	background: #31303F;
	border: 1px solid black;
  	padding: 2px;
	border-radius: 5px;
	box-shadow: 5px 5px 5px #1D1F21;
	margin-bottom: 2px;
	color: #ffffff;
}

div.post.reply {
	background: #31303F;
	max-width: 60%;
  	border-color: black;
	border: 1px solid #1d1f21;
  	padding: 5px 20px 5px 20px;
	border-radius: 25px;
	box-shadow: 5px 5px 5px #1D1F21;
	margin-bottom: 5px;
	color: #ffffff;
}


div.post.reply div.body a{
	color: #6195B8;
}

p.intro span.subject {
	color: #6195B8;
}

p.intro span.name {
	color: #dadada;
}

p.intro time {
	color: #dadada;
}

p.intro a.post_no {
	color: #dadada;
}

a, a:visited {
	color: #6195B8;
}

div.post.reply.highlighted {
	background:	#414141;
}


div.pages {
	color: #dadada;
	background-color: #282A2E;
	border: 1px solid black;
}

div.pages a.selected {
	color: #6195B8;
	font-weight: bolder;
}

table.modlog tr th {
	background-color: #414141;
}

.theme-catalog div.thread {
  	background: #31303F;
	border: 1px solid #1d1f21;
  	padding: 15px;
	border-radius: 25px;
	box-shadow: 5px 5px 5px #1D1F21;
    margin-left: 5px;
    margin-right: 5px;
	margin-bottom: 15px;
    height: 250px !important;
}

footer {
	display: none;
}
}
