css:
/* AGENT_SHEET */
@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);

scrollbar {
	-moz-appearance: none !important;
	background: /*[[scrollbar]]*/ !important;
	min-height: 16px !important;
	min-width: 16px !important;
}

scrollbarbutton {
	display: none !important;
}

thumb {
	-moz-appearance: none !important;
	border: 2px solid /*[[scrollbar]]*/ !important;
	background-color: /*[[thumb]]*/ !important;
	box-shadow: inset 0 0 0 1px /*[[border]]*/ !important;
}

thumb:hover {
	background-color: /*[[hthumb]]*/ !important;
	box-shadow: inset 0 0 0 1px /*[[hborder]]*/ !important;
}

thumb:active {
	background-color: /*[[athumb]]*/ !important;
	box-shadow: inset 0 0 0 1px /*[[aborder]]*/ !important;
}
