css:
@-moz-document domain("grupia.com")
{
	.header-container, .header
	{
	  background-color: /*[[cor_header]]*/;
	}
	.button.highlight, .side-menu-button.selected, .context-menu.show a:hover
	{
	  background-color: /*[[cor_botao]]*/ !important;
	}
	.side-menu-button.selected
	{
		color: /*[[cor_botao_texto]]*/ !important;
	}

	.comunity-title, .user-profile-dashboard h1
	{
		color: /*[[cor_titulos]]*/;
	}

	.soft-card
	{
		background-color: /*[[cor_aviso]]*/;
		color: /*[[cor_aviso_texto]]*/;
	}

	.soft-card a
	{
		color: /*[[cor_aviso_link]]*/;
	}

	.card-text a
	{
		color: /*[[cor_config_comu_link]]*/;
	}
}
