css:
@-moz-document url-prefix("https://miiverse.nintendo.net/") {

#sidebar-profile-body.with-profile-post-image .nick-name {
    padding-top: 0;
    margin-top: 10px;
    color: /*[[namecolor]]*/;
    text-shadow: 0px 0 4px /*[[nameglow]]*/;
}

.news-list .nick-name {
    font-weight: bold;
    color: /*[[namecolor]]*/;
    text-shadow: 0px 0 4px /*[[namecolor]]*/;
}

.post .user-name a {
    color: /*[[namecolor]]*/;
    font-weight: bold;                                        
    text-shadow: 0px 0 4px /*[[nameglow]]*/;
}
}
