css:
@-moz-document url-prefix("http://www.tumblr.com"), url-prefix("https://www.tumblr.com") {
.identity-refresh .selected .tab_notice {
background: #EF3232;
}
div.post_media {
    -ms-transform: scale(0.926,0.926);
    -webkit-transform: scale(0.926,0.926);
    transform: scale(0.926,0.926);
}
.identity-refresh .right_column:after{
display:none;
}
.identity-refresh {
background-color: /*[[bg]]*/;
}
.identity {
background-color: /*[[bg]]*/;
}
.identity-refresh .controls_section li:not(.section_header):hover {
background-color: /*[[bg]]*/;
}
.identity-refresh .l-container.l-container--two-column-dashboard .left_column {
background-color: /*[[bg]]*/;
}
.identity-refresh .l-container.l-container--two-column .l-content, .identity-refresh .l-container.l-container--two-column-dashboard .l-content {
background-color: /*[[bg]]*/;
}
.identity-refresh .l-container.l-container--two-column .right_column, .identity-refresh .l-container.l-container--two-column-dashboard .right_column {
background-color: /*[[bg]]*/;
}
 
.identity-refresh .controls_section li:not(.section_header):hover {
background-color: /*[[hihg]]*/;
}
}
