css:
@-moz-document url("http://www.tumblr.com/"), url-prefix("http://www.tumblr.com/"), url-prefix("https://www.tumblr.com/") {
* { 
    color:/*[[color]]*/!important;
    text-shadow: none !important;
}
/*[[palette]]*/ /*[[blue]]*/ /*[[gold]]*/ /*[[red]*/ /*[[violet]]*/ /*[[lime]]*/ /*[[damask]]*/ /*[[slate]]*/ /*[[apricot]]*/ /*[[lilac]]*/ /*[[mint]]*/

.flag--wide-right-column .l-container--two-column .right_column, .flag--wide-right-column .l-container--two-column-dashboard .right_column {
    width:245px;
    left:5px;
}
.flag--wide-right-column .l-container--two-column-dashboard, .flag--wide-right-column .l-container--two-column {
    width:900px;
}
#following {
    padding: 0 0 0 0;
}
#tabs.tabs_3 .tab:last-child {
    width: 270px;
}
.tab_bar .tab.selected .tab_anchor {
    opacity:.6!important;
}
.tab_bar .tab.selected .tab_anchor:hover, .tab_bar .iconic .tab_anchor:hover {
    opacity:.8!important;
}
ol#posts li.notification {
    background-color: #f9f9f9 !important;
    border-bottom: none !important;
    color: #f9f9f9 !important;
}
#right_column:after,
#right_column,
#left_column {
    padding: 20px 0 !important;
}
.logo_dot {
    background: none!important;
    opacity: 0.0 !important;
    filter: alpha(opacity=0);
    background-image: none !important;
}
#right_column:after,
#right_column,
#left_column,
.post_content,
.post_tags:after,
.section.first.divider,
blockquote pre,
#tumblr_radar {
    background: none !important;
}
.search_popover {
    margin-right: -40px;
    margin-top:-6px;
    border: none !important;
    border-top: none !important;
}
.identity-refresh .send_fan_mail {
    display: block !important;
    padding: 8px 0;
    margin: 0 0 20px;
    color: #fff;
    text-align: center;
    font-size: 15px;
    font-weight: 700;
    text-decoration: none;
    -webkit-font-smoothing: antialiased;
    border-radius: 0px;
    background: none !important;
}
#right_column a#dashboard_switch_blog_menu_current {
    background: #f9f9f9 !important;
    opacity: 0.90 !important;
    border: 0px !important;
}
#right_column:hover {
    display: block;
    border-bottom: none !important;
    background: none!important;
    background-image: none !important;
    border: none !important;
}
.election-day-voted .popover_blogs {
    margin-top: 66px;
    position: relative;
    opacity: 0.8 !important;
}
.identity-refresh .popover_blogs {
    margin-top: 66px;
    opacity: 0.8 !important;
}
.election-day-voted .popover_blogs:hover,
.identity-refresh .popover_blogs:hover {
    background: none !important;
    opacity: 0.8 !important;
}
.search_popover {
    background: none !important;
    opacity: 0.8 !important;
}
.search_popover:hover {
    background: none !important;
    opacity: 0.8 !important;
}
.identity-refresh .controls_section li:not(.popover_blogs):hover {
    background: none !important;
    opacity: 1.0 !important;
    text-shadow: 2px !important;
    color: #818181 !important;
}
.identity-refresh .controls_section li:not(.popover_blogs) {
    background: none !important;
    opacity: 0.6 !important;
}
.identity-refresh .controls_section.user_list li .follow_list_item_blog:before {
    background: none!important;
    opacity: 0.0!important;
    filter: alpha(opacity=0)!important;
    background-image: none !important;
}
#left_column .left_column {
    background: none!important;
    opacity: 0.0!important;
    filter: alpha(opacity=0)!important;
    background-image: none !important;
}
.post_avatar {
    opacity: 1.0 !important;
    filter: alpha(opacity=100)!important;
    background: none !important;
}
#dashboard_switch_blog_menu_current,
#dashboard_switch_blog_arrow_and_divider,
#dashboard_switch_blog_arrow_current {
    background-image: none !important;
}
#permalink {
    background-image: none !important;
}
.l-header .selection_nipple {
    display: block;
    border-bottom: none !important;
    display: none !important;
    background: none !important;
    border: 0px !important
}
.l-header .selection_nipple:hover {
    display: block;
    border-bottom: none !important;
    display: none !important;
    background: none !important;
    border: 0px !important
}
.l-header-container {
    display: block;
    border-bottom: none !important;
    border: 0px !important;
    background: none!important;
    background-image: none !important;
}
.l-header-container--refresh {
    display: block;
    border-bottom: none !important;
    border: 0px !important;
    background: none!important;
    background-image: none !important;
}
.compose-button {
    display: block;
    border-bottom: none !important;
    border: 0px !important;
    background: none!important;
    background-image: none !important;
    opacity: 0.6!important;
    filter: alpha(opacity=60);
}
.compose-button .notification_type_icon {
    display: none !important;
}
.search_form_row {
    opacity: 0.6!important;
    filter: alpha(opacity=60);
    background-image: none !important;
}


/*[[colors]]*/ /*[[cblue]]*/ /*[[cgold]]*/ /*[[cred]*/ /*[[cviolet]]*/ /*[[clime]]*/ /*[[cdamask]]*/ /*[[cslate]]*/ /*[[capricot]]*/ /*[[clilac]]*/ /*[[cmint]]*/

#new_post_notice_container {
    color: #fff !important
}
#new_post_notice_container a {
    color: #fff !important
}
#content {
    padding: 10px 0;
}
}
