css:
@-moz-document url("http://www.tumblr.com/"), url-prefix("http://www.tumblr.com/"), url-prefix("https://www.tumblr.com/") {
.post.new_post .new_post_label{background:none !important;}

a.new_post_label > i {
  height: 90px;margin-top:-5px;
}
.new_post_label {
  padding: 71px 1px 8px 0 !important;
  font-size: 11px !important;
  text-transform: lowercase;
}
/*[[colors]]*/ /*[[dark]]*/ /*[[wgr]]*/ /*[[wbl]]*/ /*[[raw]]*/ /*[[row]]*/ /*[[viow]]*/ /*[[mlb]]*/  /*[[dblg]]*/ /*[[dgaw]]*/

.new_post_label > I {
  color: transparent !important;
  transition: background-position .08s linear !important;}
#new_post_label_text > I {background-position: -3px -5px !important;}
#new_post_label_photo > I {background-position: -75px -5px !important;}
#new_post_label_quote > I {background-position: -146px -5px !important;}
#new_post_label_link > I {background-position: -218px -5px  !important;}
#new_post_label_chat > I {background-position: -290px -5px !important;}
#new_post_label_audio > I {background-position: -364px -5px  !important;}
#new_post_label_video > I {background-position: -436px -5px  !important;}

#new_post_label_text:hover > I {background-position: -3px -9px !important;}
#new_post_label_photo:hover > I {background-position: -75px -9px !important;}
#new_post_label_quote:hover > I {background-position: -146px -9px !important;}
#new_post_label_link:hover > I {background-position: -218px -9px  !important;}
#new_post_label_chat:hover > I {background-position: -290px -9px !important;}
#new_post_label_audio:hover > I {background-position: -364px -9px  !important;}
#new_post_label_video:hover > I {background-position: -436px -9px  !important;}

#new_post_label_text:active > I {background-position: -3px -5px !important;}
#new_post_label_photo:active > I {background-position: -75px -5px !important;}
#new_post_label_quote:active > I {background-position: -146px -5px !important;}
#new_post_label_link:active > I {background-position: -218px -5px  !important;}
#new_post_label_chat:active > I {background-position: -290px -5px !important;}
#new_post_label_audio:active > I {background-position: -364px -5px !important;}
#new_post_label_video:active > I {background-position: -436px -5px !important;}
}
