css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("inoreader.com") {

/* use standard font everywhere: */
* {
    font-family: sans-serif;
}

/* recolor top panel: */
#subscriptions_buttons,
#sb_rp_tools
  {
    background-color: #d1b9ee;
    background-image: none;
  }

/* recolor side panel: */
#tree_pane,
#sb_tree_part,
#sb_menu_icon,
#sb_tp_search_overlay {
    background-color: #5e468c;
    background-image: none;
}


/* list style */
.article_header{
  margin-left: 0;
}
.article_unread_dot {
  margin-left: 6px;
  margin-right: 25px;
}
.article_unread_dot_external {
  width: 30px;
  height: 25px;
  top: -2px;
  left: 0;
}
.article_unread_dot_internal{
  width: 18px;
  height: 15px;
  background-color: #d0d7e244;
}
.article_unreaded .article_unread_dot_internal{
  background-color: #d0a7e2;
}

}
