css:
@-moz-document url-prefix(http://www.tumblr.com/),url-prefix(https://www.tumblr.com/){

.post_media {
    max-width: 540px!Important;
    min-width: 500px!Important;
}
.post_full.is_full_width.is_photo {
    height: auto!important;
    max-width: 540px!important;
    min-width: 500px;
}
.post_full.is_photoset .photoset .photoset_row .photoset_photo img {
    width: auto!important;
}
.post .post_content img {
    max-width: 540px;
    height: auto!important;
}
.post_media_photo.image {
    width: auto!Important;
}
.post_media .audio_player_container {
    margin: 0 20px!important;
}
.post_media .photoset,
.post_media .post_media_photo {
    text-align: center;
    margin: auto;
}
.photoset_row {
    max-width: 540px!important;
    min-width: 500px!Important;
    height: auto!Important;
}
.post_media {
    text-align: center;
}

#search_posts .post_media{
    text-align:left;
    min-width: 300px!important;
}

.photoset_row_1 img {
    max-width: 540px!Important;
    min-width: 500px!Important;
}
.photoset_row_2 img,
.photoset_row.photoset_row_2 img,
.photoset_row.photoset_row_2>a>img,
.photoset_row.photoset_row_2>img {
    max-width: 268px!important;
    min-width: 245px!Important;
}
.photoset_row_3 img,
.photoset_row.photoset_row_3 img,
.photoset_row.photoset_row_3>a>img,
.photoset_row.photoset_row_3>img {
    max-width: 177px!important;
    min-width: 160px!Important;
}
.post_full.is_full_width.is_photoset .photoset .photoset_row .photoset_photo {
    margin-left: auto;
    margin-right: auto;
}
.post_full.is_full_width.is_photoset .photoset .photoset_row,
.post_full.is_full_width.is_photo .photoset .photoset_row {
    margin-top: 10px;
}
.photoset_row.photoset_row_1>a>img {
    max-width: 540px!important;
    min-width: 500px!important;
}
.post_full.is_photoset .photoset .photoset_row .photoset_photo {
    margin-left: 4px!important;
}
.post_full.is_photoset .photoset .photoset_row:first-child,
.post_full.is_photo .photoset .photoset_row:first-child {
    margin-top: 0!important;
}
.post_full.is_photoset .photoset .photoset_row,
.post_full.is_photo .photoset .photoset_row {
    margin-top: 0px!important;
    margin-bottom: 0px!important;
}
.post .post_content blockquote img {
    max-width: 480px!important;
}
.post_full .post_body .inline_image {
    max-width: 100%!important;
    height: auto!important;
}
.post .post_content .reblog-content figure img{
    max-width:500px!important;
}

.post .post_content .reblog-content  img{
    max-width:500px!important;
}

.post_media.post_media_small {
    margin: 13px 0 0;
    
}

.quote_source_table .quote_source img{
    max-width: 500px!important;
    
}

}
