css:
@-moz-document url-prefix("http://web.roblox.com"), url-prefix("http://www.roblox.com"), url-prefix("https://web.roblox.com"), url-prefix("https://www.roblox.com") {
/* Everything else. */

 
#MasterContainer, #Footer, .nav-content, html {
background: transparent;
}

#Footer > div.FooterNav {
color: white;  
}
   
body {
	background:url("/*[[backgroundUrl]]*/") no-repeat fixed; 

  /* ^ You can change this setting on the UserStyles page. ^ */
  
    margin-top: 10px;
    font-size: 10pt;
    background-size: cover;
    font-family: Segoe UI;
}

#Footer.footer-container {
  background: none;
}

#navigation > ul > li.rbx-lead:before {
	content: "/*[[navTop]]*/"; /* Changeable :-) */
    visibility: visible
}

#header {
     background: /*[[headerCol]]*/
    }
    
#navigation {
     background: /*[[navCol]]*/
    }


#navigation > ul > li.rbx-lead {
     visibility: hidden;
    text-align: center
}

#navigation > ul > li.rbx-lead:after {
  content: "/*[[navBelow]]*/"; 
/* changeable */

  text-align: center;
  visibility: visible;
  position: inherit;
}
    
input[type=text] {
	border: 1px solid #A7A7A7;
}

  #upgrade-now-button {
  background-color: darkgoldenrod    
  }
}
