css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("youtube.com") {
  /* 
  @media screen and (max-width: 999px) {
    #content #main:hover {
      max-height: calc(100vh - 560px) !important;
      overflow-x: hidden !important;
      overflow-y: scroll !important;
    }
  }
   */
  @media screen and (min-width: 1000px) {
    #content #meta {
      height: 70px !important;
      overflow-y: hidden !important;
      border-bottom: 2px dotted #bbb;
    }
    #content #meta:hover {
      height: auto !important;
      transition: height 0.2s ease-in-out 0.1s !important;
    }
    #content #comments:hover {
      max-height: calc(100vh - 600px) !important;
      overflow-x: hidden !important;
      overflow-y: scroll !important;
    }
  }

  @media screen and (min-width: 1294px) and (min-height: 630px) {
    #content #comments:hover {
      max-height: calc(100vh - 720px) !important;
    }
  }
  @media screen and (min-width: 1720px) and (min-height: 980px) {
    #content #comments:hover {
      max-height: calc(100vh - 970px) !important;
    }
  }

}
