css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("userstyles.org") {
/* ESI 2.5.1 core */
html:not(.no-such-class):not(.nope)  {font-size: /*[[fs]]*/ !important; line-height: /*[[lh]]*/ !important}
esi-widest-text-block {max-width: /*[[mw]]*/ !important}
a {text-decoration: none !important}
esi-text-block {width: x rem; height: y rem}	
* {font-size: inherit !important; line-height: inherit !important}
html:not(.no-such-class):not(.nope) h1 {font-size: 1.7 rem !important; font-size: calc(1.4375rem + 8px) !important;
	line-height: 120% !important; line-height: calc(1.4375rem * 1.052 + 8px * 1.052 + 5px) !important}
html:not(.no-such-class):not(.nope) h2 {font-size: 1.4 rem !important; font-size: calc(1.25rem + 4px) !important;
	line-height: 125% !important; line-height: calc(1.25rem * 1.052 + 1.052 + 4px + 5px) !important}
html:not(.no-such-class):not(.nope) h3 {font-size: 1.17 rem !important; font-size: calc(1.0625rem + 2px) !important;
	line-height: 130% !important; line-height: calc(1.0625rem * 1.052 + 2px * 1.052 + 5px) !important}
html:not(.no-such-class):not(.nope) sup, html:not(.no-such-class):not(.nope) sub{line-height: 1 !important; font-size: 0.6875rem; font-size: calc(1rem - 5px) !important;}
/*ESI core end*/


.listing-left-info {
    width: 355px !important;
}
#left-info {
    width: 390px !important;
}
body {
    max-width: /*[[mw]]*/  !important;
}

a:hover {
    background: #E1E7ED !important;
    text-decoration: none !important;
    -webkit-border-radius: 2px !important;
    -khtml-border-radius: 2px !important;
    -moz-border-radius: 2px !important;
    border-radius: 2px !important;
}

a {
    padding-right: 3px !important;
    padding-left: 3px !important;
    text-decoration: none !important;
    color: /*[[ac]]*/ !important;
}

#left-sidebar {
  width: 12rem !important;
}
#main-article {
  margin-left: 12.5rem !important;
}

.style-setting-option {
    width: /*[[sw]]*/;
}
}
