css:
/*AGENT SHEET*/
@-moz-document url-prefix("about:stylish-edit"),url-prefix("data:text/html") {

/*[[change_font]]*/

/*[[button_layout]]*/

/*[[add_scrollbars]]*/

/*___________________________________________________________________
 |                                                                   |
 |  DISABLED BUTTONS SHOW LOW CONTRAST TEXT INSTEAD OF TRANSPARENCY  |
 |___________________________________________________________________|*/

#stylish-edit.theme-body :-moz-any(#save-button,#preview-button,#check-for-errors-button)[disabled="true"] { opacity: 1 !important;}
#stylish-edit.theme-body.theme-dark :-moz-any(#save-button,#preview-button,#check-for-errors-button)[disabled="true"] .button-text  { color: #505A64 }
#stylish-edit.theme-body.theme-light :-moz-any(#save-button,#preview-button,#check-for-errors-button)[disabled="true"] .button-text { color: #AFB4C3 }
    
/*___________________________________________________
 |                                                   |
 | REDUCES BRIGHTNESS OF TEXTBOX TEXT FOR DARK THEME |
 |___________________________________________________|*/   
    
#stylish-edit.theme-body.theme-dark #name { color: #A0AAB4 !important;}    

/*___________________________
 |                           |
 | SHORTENS WRAP LINES LABEL |
 |___________________________|*/

#stylish-edit.theme-body #wrap-lines > hbox:after {
    content: 'Wrap' ;
    display: -moz-box ;
    margin: 0 0px 0 -1px }
#stylish-edit.theme-body #wrap-lines > hbox:before {
    content: '__' ;
    display: -moz-box ;
    font: normal 10px Segoe UI ;
    margin: 3px -10px 0 0 }
#stylish-edit.theme-body #wrap-lines > hbox > label { display: none }
#stylish-edit.theme-body #wrap-lines > image   { margin-right: 3px; opacity: .8 }
#stylish-edit.theme-body.theme-dark #wrap-lines > hbox:before  { color: #b4b4b4 }
#stylish-edit.theme-body.theme-dark #wrap-lines > hbox:after   { color: #c8c8c8 }
#stylish-edit.theme-body.theme-light #wrap-lines > hbox:before { color: #505050 }
#stylish-edit.theme-body.theme-light #wrap-lines > hbox:after  { color: #646464 }
#stylish-edit.theme-body.theme-dark #wrap-lines > image { -moz-appearance: none !important; }

/*[[label_style]]*/
}
/*[[hide_autohide]]*/
