css:
@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);

#window-controls {
  margin-top: /*[[mtop]]*/;
  margin-right: /*[[mright]]*/;
}

#window-controls toolbarbutton {
  -moz-appearance: none;
  margin-left: /*[[buttonspacing]]*/;

/*[[whichtheme]]*/
    /* all themes: http://pastebin.com/CA7E3W6K */

  --tbHover: 0px;
  --tbHover2: 20px;
  width: 20px;
  height: 20px;
  padding: 0;
}

#window-controls toolbarbutton:hover {
  --tbHover: 20px;
  --tbHover2: 40px;
}

#window-controls toolbarbutton:active {
  --tbHover: 40px;
  --tbHover2: 60px;
}

#minimize-button {
  -moz-image-region: rect(var(--tbHover) 20px var(--tbHover2) 0px);
}

#restore-button {
  -moz-image-region: rect(var(--tbHover) 40px var(--tbHover2) 20px);
}

#close-button {
  -moz-image-region: rect(var(--tbHover) 60px var(--tbHover2) 40px);
}
