css:
@-moz-document domain("tumblr.com") {
    .compose-button {
    border-color: /*[[composeborder]]*/;
    background: /*[[composebackground]]*/;
    opacity: /*[[composeopacity]]*/;
    }
    .compose-button:before{
    color: /*[[composeicon]]*/;
    }
    .l-header-container--refresh {
    background: linear-gradient(/*[[banner1]]*/, /*[[banner2]]*/);
    opacity: /*[[opacity]]*/;
    }
}
