css:
@-moz-document url-prefix("http://www.tumblr.com/"), url-prefix("https://www.tumblr.com/") {
.identity-refresh .post_avatar.fixed-avatar {position: absolute!Important; top: 0!Important; left: -85px!important;}
.identity-refresh .post_avatar.bottom-avatar {left: -85px!Important; top: 0 !important; bottom: inherit!Important;}
.post_avatar.post-avatar--sticky .avatar-wrapper {position: absolute !important; top: 0px; height: auto; width: auto;}
.post_avatar.post-avatar--fixed {position: absolute!Important; top: 0!Important; left: -85px!important;}
.post_avatar.post-avatar--absolute {position: absolute!Important; top: 0!Important; left: -85px!important;}
.post_avatar.post-avatar--sticky .post_avatar_wrapper {position: relative!important; top: 0!important;}

}
