*/@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url("https://www.facebook.com/"), url-prefix("https://www.facebook.com/"), url-prefix("https://www.facebook.com/index.php?stype=lo"), url-prefix("https://www.facebook.com/index.php"), url-prefix("https://www.facebook.com/login.php?"), url-prefix("https://www.facebook.com/login.php"), url-prefix("https://www.facebook.com/login.php?next="), url-prefix("https://www.facebook.com/login.php?login_attempt=") {
body.fbIndex,body.login_page
{ 
  background:  url('/*[[background]]*/') center center  no-repeat fixed !important;
  background-size: cover !important;
  -webkit-background-size: cover; 
  background-repeat: no-repeat !important;
}
.signupBanner .signup_btn {
    left: 450px;
    position: absolute;
    top: 98px;
}
.loggedout_menubar_container 
{
  background: url('http://k31.kn3.net/AE64C7C1C.png') !important; 
}
.fbIndex #globalContainer #dropmenu_container,.fbIndex #globalContainer #content,.fbIndex #globalContainer #pageFooter,#pageFooter,.loggedout_menubar .fb_logo
{
display: none!important
}
table
{background: rgba(150,150,150,0.2) !important;
    padding: 5px 15px 5px 0px;
}
.fbIndex .loggedout_menubar_container {
  position: fixed !important;
    top: 110% !important;
    left: 50% !important;
  margin-top: -150px !important;
  margin-left: -210px !important;
	background: black;
}
.fbIndex .loggedout_menubar_container .lfloat,.fbIndex .loggedout_menubar_container .rfloat 
{ 
  float: none !important 
}
input#email.inputtext,input#pass.inputtext
  {
    color: #F5E25B;
    border-bottom: 2px solid rgba(255,255,255,0.2) !important;
    border: 0px;
    background:transparent;
  }
  ._4-u5 {
    background-color: rgba(0, 0, 0, 0);
}
._53jh {
    background-color: rgba(0, 0, 0, 0);
    background-image: none;
    border-bottom: 0px solid #133783;
}
input:-webkit-autofill,
input:-webkit-autofill:hover, 
input:-webkit-autofill:focus
input:-webkit-autofill, 
textarea:-webkit-autofill,
textarea:-webkit-autofill:hover
textarea:-webkit-autofill:focus,
select:-webkit-autofill,
select:-webkit-autofill:hover,
select:-webkit-autofill:focus {
-webkit-text-fill-color: #FADA39;
  -webkit-box-shadow: 0 0 0px 1000px #000 inset;
  transition: background-color 5000s ease-in-out 0s;
}
.uiButtonConfirm {
    background-color: rgba(15,15,15,0.5);
    border-color: rgba(0, 0, 0, 0);
}
.uiButtonConfirm:active, .uiButtonConfirm.uiButtonDepressed {
    background-color: rgba(15,15,15,0.9);
    border-color: rgba(0, 0, 0, 0);
}

:focus 
{   outline-width:0px;}

}
