css:
@-moz-document url("https://web.whatsapp.com/") {
.app-wrapper
{
	background-color:#4B4B4C;
  	
  	
        background-image: url("/*[[bg]]*/") !important;
        background-size:cover;
        background-repeat:no-repeat;
}


.app-wrapper::before
{
	background-color:#282828;  
  	background-color:transparent;
  	
}


.pane-list-header 
{
	color:black;
}

.message-out:before
{
	background-image:none;
}
.message-out
{
		background-color:#E0E0E0;
}


.drawer-container-right, .pane-chat, .pane-intro
{
	background-image:none;
  	background-color:lightgray;
}
.chat-title, .chat-secondary, .chat-status
{
	
}

#side, #main
{
	border-radius:3px;
}
#window, .app, .btn-more, .btn-plain:hover
{
	box-shadow:none;
}
.app, .two
{
	background-color:transparent;
    background-repeat:no-repeat;
  
}

.drawer-container-right, .pane-chat, .pane-intro
{
	margin-left: 40px;

}
.drawer-container-left, .pane-list
{
	width:349px;	
}
}
