css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("web.telegram.org") {
    body, .modal-content{
    background-color: black !important;
    color:#cccccc !important;
    }
    a.tg_checkbox {
        color: #ccc !important;
    }

    .im_page_wrap{ /*background color of contentwrapper under header*/
        background-color: #181818 !important;
        border-left: 1px solid /*[[decorationColor]]*/ !important;
        border-right: 1px solid /*[[decorationColor]]*/ !important;
        border-bottom: 1px solid /*[[decorationColor]]*/ !important;
        box-shadow: none;
    }

    .im_dialog_peer { /*peer nickname unselected*/
        color:#cccccc !important;
    }
    
    .contacts_modal_members_list a.contacts_modal_contact { /*nicknames in contactlist via hamburger menu*/
       color: #cccccc;
    }


    .active a.im_dialog { /*selected peer */
    background-color: /*[[decorationColor]]*/ !important;
    }

    .tg_head_split, .md_modal_head{ /*page header block*/
    background-color: /*[[decorationColor]]*/ !important;
    }

    .dropdown.open .tg_head_btn, .tg_head_btn:hover { 
        background: /*[[decorationColor]]*/ !important;;
    }


    .form-control { /*input field*/
    background-color: #0e0e0e !important;
    color: #ffffff !important;
    }

    .im_dialogs_col_wrap { /*contactlist right border*/
        border-right: 2px solid /*[[decorationColor]]*/; 
    }

    .im_dialogs_col .nano > .nano-pane > .nano-slider { /*scrollbarbutton when hover*/
        background-color: /*[[decorationColor]]*/ !important;
    }


    a:hover {
      background-color: #282828 !important;
    }

        /*emoji theming:*/

    .composer_emoji_tooltip, .icon-tooltip-tail, .dropdown-menu  {
        background-color: black !important;
        border-color: /*[[decorationColor]]*/;
    }   
    
}
