css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("opera.com") {
/* i really want this to be global */

/* ESI 2.4 core */
html {font-size: /*[[fs]]*/ !important; line-height: /*[[lh]]*/ !important}
esi-widest-text-block {max-width: /*[[mw]]*/ !important}
a {text-decoration: /*[[ld]]*/; color: /*[[lc]]*/}
esi-text-block {width: x rem; height: y rem}
* {font-size: inherit !important; line-height: inherit !important}
h1 {font-size: 1.5rem !important; line-height: calc(1.5rem * 1.052 + 5px) !important}
h2 {font-size: 1.3rem !important; line-height: calc(1.3rem * 1.052 + 5px) !important}
h3 {font-size: 1.17rem !important; line-height: calc(1.17rem * 1.052 + 5px) !important}
body, body, .site-message, .messages>.message, .drop-down .drop-down-btn:hover,.drop-down .drop-down-btn:focus, .drop-down .drop-down-pane, input {background-color: /*[[bc]]*/ !important; color: /*[[fc]]*/ !important}
esi-text-block {background-color: rgba(x, y, z, 0) !important}
/*ESI-end*/
.listing .listing-image{max-height: unset !important}
#page-wrapper>header h1 a{color: /*[[fc]]*/ !important}
.scroll-container{height: 24rem !important}
#page-wrapper{max-width:/*[[mw]]*/}
.grid .span-one-third, .grid .span-two-third, .grid .span-half, #page-wrapper>header nav.opera>div, .listing .grid .span-one-third, .listing .listing-image, .scroll-container, 	#main.pkg-details .content .sidebar, #main.pkg-details .image-viewer ul, .image-viewer ul li{max-width: unset}
}
