css:
@-moz-document url("https://www.tumblr.com/dashboard"), url("http://www.tumblr.com"), url("https://www.tumblr.com/"), url-prefix("http://www.tumblr.com/"), url-prefix("https://www.tumblr.com/") {
* {
    text-shadow: none !important;
    color: #4c4c4c !important;
}
.post-forms-glass.post-forms-glass_active.active {
    background-color: rgba(33, 184, 191, .85);
}
.svg-logo {
    background-image: url('http://i.imgur.com/h8pumUS.png /*[[logo1]]*/ /*[[logo2]]*/ /*[[logo3]]*/ /*[[logo4]]*/') !important;
    background-size: contain !important;
    width: 100px!important;
}

.tab_bar 
	{
    right: 110px!important;
	}

.popover_menu_item .thumb { 
    right: 33px !important;
	}

.search_popover * {
  box-sizing: border-box;
  width: 188px;
}

.logo .large-shadow,
.logo .small-shadow,
.logo .outer,
.logo .inner {
    display: none!important;
}
.logo .svg-logo {
    padding: 9px 8px 6px 20px!important;
}
     
/*[[home]]*/ /*[[home1]]*/ /*[[home2]]*/ /*[[none]]*/

/*[[dash]]*/ /*[[dash1]]*/ /*[[dash2]]*/  /*[[dash3]]*/


ol#posts li.notification {
    background-color: #f9f9f9 !important;
    border-bottom: none !important;
    color: #f9f9f9 !important;
}
#right_column:after,
#right_column,
#left_column {
    padding: 20px 0 !important;
}
.logo_dot {
    background-color: inherit!important;
    background: none!important;
    opacity: 0.0!important;
    filter: alpha(opacity=0)!important;
    background-image: none !important;
}
#right_column:after,
#right_column,
#left_column,
.post_content,
.post_tags:after,
.section.first.divider,
blockquote pre,
#tumblr_radar {
    background: none !important;
}
.search_popover {
    margin-right: -40px;
    border: none !important;
    border-top: none !important;
}
.identity-refresh .send_fan_mail {
    display: block !important;
    padding: 8px 0;
    margin: 0 0 20px;
    color: #fff;
    text-align: center;
    font-size: 15px;
    font-weight: 700;
    text-decoration: none;
    -webkit-font-smoothing: antialiased;
    border-radius: 0px;
    background: none;
    background-color: inherit !important;
}
.send_fan_mail:hover {
    background: #83D3D6 !important;
    opacity: 0.70 !important;
}
#right_column a#dashboard_switch_blog_menu_current {
    background: #f9f9f9 !important;
    opacity: 0.90 !important;
    border: 0px !important;
}
#right_column:hover {
    display: block;
    background-color: inherit !important;
    border-bottom: 0px !important;
    background: none!important;
    background-image: none !important;
    border: none !important;
}
.election-day-voted .popover_blogs {
    margin-top: 66px;
    position: relative;
    opacity: 0.8 !important;
}
.identity-refresh .popover_blogs {
    margin-top: 66px;
    opacity: 0.8 !important;
}
.election-day-voted .popover_blogs:hover,
.identity-refresh .popover_blogs:hover {
    background-color: inherit !important;
    opacity: 0.8 !important;
}
.search_popover {
    background-color: inherit !important;
    opacity: 0.8 !important;
}
.search_popover:hover {
    background-color: inherit !important;
    opacity: 0.8 !important;
}
.popover_menu_item:hover,
.search_popover .popover_menu_item_link:hover,
.search_popover .explore_topics_link:hover,
.search_popover .result_link:hover {
    background: none !important;
    background-color: #83D3D6 !important;
    opacity: 0.8 !important;
}
.identity-refresh .controls_section li:not(.popover_blogs):hover {
    background-color: inherit !important;
    background: none !important;
    opacity: 0.6 !important;
    text-shadow: 2px !important;
}
.l-header-container--refresh .tab_bar .tab .tab_anchor {
    opacity: 0.9 !important;
    color: #fff !important;
}
.l-header-container--refresh .tab_bar .tab .tab_anchor:hover {
    opacity: 0.7 !important;
    color: #fff !important;
}
.l-header-container--refresh .tab_bar .tab_nav_account .tab_anchor i:before {
    color: #fff !important;
}
.identity-refresh .controls_section li:not(.popover_blogs) {
    background-color: inherit !important;
    background: none !important;
    opacity: 0.8 !important;
}
.discover-controls-wrapper {
    display: block;
    border-bottom: none !important;
    background-color: inherit !important;
    background: none!important;
    background-image: none !important;
    border: none !important;
    opacity: 0 !important;
}
#nav .nav_item.active {
    background-color: #83D3D6;
}
.identity-refresh .controls_section.user_list li .follow_list_item_blog:before {
    background-color: inherit!important;
    background: none!important;
    opacity: 0.0!important;
    filter: alpha(opacity=0)!important;
    background-image: none !important;
}
#left_column #posts .post .permalink {
    background-color: #83D3D6 !important;
    opacity: 0.90 !important;
}
#left_column .left_column {
    background-color: inherit!important;
    background: none!important;
    opacity: 0.0!important;
    filter: alpha(opacity=0)!important;
    background-image: none !important;
}
.popover_menu_item_tracked_tag:hover {
    background: #83D3D6 !important;
}
#left_column #posts .notification {
    background-color: inherit !important;
    opacity: 0.88 !important;
}
.post_avatar {
    opacity: 1.0 !important;
    filter: alpha(opacity=100)!important;
    background-color: inherit !important;
}
.post_avatar .post_avatar_link {
    border-radius: 0px !important;
}
.post_avatar_link {
    opacity: 0.80 !important;
    filter: alpha(opacity=80)!important;
    background-color: #83D3D6 !important;
}
.post_avatar_link:hover {
    opacity: 1.0 !important;
    filter: alpha(opacity=100)!important;
    background-color: #83D3D6 !important;
}
.post_tags {
    margin-top: 0px !important;
}
.post_content {
    padding-bottom: 10px !important;
}
#nav .nav_item.active,
#nav .nav_item .nav_menu,
#nav .nav_item .nav_item_nipple .nav_item_nipple_pixel {
    background-color: #f9f9f9 !important;
    background-image: none !important;
    border: 0px !important;
}
#dashboard_switch_blog_menu_current,
#dashboard_switch_blog_arrow_and_divider,
#dashboard_switch_blog_arrow_current {
    background-image: none !important;
}
#permalink {
    background-image: none !important;
}
.identity-refresh .post_full .post_permalink {
    border-color: #83D3D6 #83D3D6 transparent transparent;
    border-radius: 0 0 0 5px;
    border-style: solid;
    position: absolute;
    border-width: 0px;
    background: #ccc;
    display: block;
    opacity: 0;
    height: 0;
    width: 0;
    right: 0;
    top: 0;
    -webkit-transition: border-width 150ms ease-in, opacity 0 ease-in 150ms;
    -moz-transition: border-width 150ms ease-in, opacity 0ms ease-in 150ms;
    transition: border-width 150ms ease-in, opacity 0ms ease-in 150ms;
}
.l-header .selection_nipple {
    border-bottom: none !important;
    display: none !important;
    background-color: inherit !important;
    border: 0px !important
}
.l-header .selection_nipple:hover {
    border-bottom: none !important;
    display: none !important;
    background-color: inherit !important;
    border: 0px !important
}
.l-header-container {
    display: block;
    border-bottom: none !important;
    background-color: inherit !important;
    border: 0px !important;
    background: none!important;
    background-image: none !important;
}
.l-header-container--refresh {
    display: block;
    border-bottom: none !important;
    background-color: inherit !important;
    border: 0px !important;
    background: none!important;
    background-image: none !important;
}
.compose-button {
    display: block;
    border-bottom: none !important;
    background-color: inherit !important;
    border: 0px !important;
    background: none!important;
    background-image: none !important;
    opacity: 0.9!important;
    filter: alpha(opacity=90);
    fill:#fff;
}
.compose-button:hover {
    opacity: 0.7!important;
    filter: alpha(opacity=70);
}
.notification_type_icon {
    display: none !important;
}
.search_form_row {
    background-color: inherit!important;
    opacity: 0.9!important;
    filter: alpha(opacity=90);
    background-image: none !important;
}
.ui_search .search_form_field:before {
    left:3px!important;
    top:6px!important;
}
.ui_search .search_query {
    background-color: #f9f9f9 !important;
    opacity: 0.50;
}
.ui_search .search_query:hover {
    background-color: #f9f9f9 !important;
    opacity: 0.90;
}
#left_column #posts .notification {
    background-color: #83D3D6 !important;
    color: #666 !important;
    border: 0px solid;
    border-color: #21B8BF !important;
    opacity: 0.50;
}
.video_preview.no_thumbnail,
.link_button {
    background-color: #83D3D6 !important;
    border: none !important;
}
.chrome.blue {
    background: none !important;
    border: none !important;
    opacity: 0.60;
    font-size: 14px;
    background-color: #21B8BF !important;
}
.chrome.blue:hover {
    opacity: 0.80 !important;
    background-color: #21B8BF !important;
}
}
