css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("memrise.com") {

  .pink-border,
  .diff,
  .premium-menu,
  li a[href*="difficult-words"],
  li a[href*="difficult-items"],
  li a[href*="learning-performance"],
  .btn-facebook,
  .btn-flacebook,
  .btn-twitter,
  .btn-gplus,
  .share-box,
  .social-buttons,
  .fb-like,
  .fb-buttons,
  .btn-pink,
  .app-promo-box,
  .recommended-courses-wrap,
  .memrise-prize,
  .nav-item-btn--premium{
    display:none !important;
  }
  
  /* fix broken header row button positions */
  .nav-item-btn--browse,
  .nav-item-btn--create{
    position:relative;
    top:-23px;
  }
  
  
}
