@-moz-document domain("t411.ai"), domain("t411.li") {

:focus {outline: none !important;}
a, .register a{color:/*[[colordark]]*/;}

/* Sommaire

	* headerPlace
		* loginWrapper
			* userBox
		* headWrapper
			* head
	* wrapper
	
 */

 
/* headerPlace ***********************************************************************/

/* loginWrapper **********************************************************************/
.headerPlace {min-width: 0;}

.loginWrapper {display:none;overflow:visible;position:fixed;top:120px;min-height:0;z-index:1000000000;background:none;}
.active .loginWrapper {display:block !important;top:0px}

.userBox, .loginBox {position:fixed;background:white;box-shadow:0 0 0 10000px rgba(0,0,0,.5);}
/** Menu personnel **/
.userBox {height:390px;width:500px;top: calc(25% - 115px);left:calc(50% - 320px);padding:40px 60px 30px 80px;border-radius:2px;}
.userBox .column {width:auto !important;}
.userBox .column:nth-of-type(3) {margin-left:120px;}
.userBox .column li{height:45px;line-height:45px;margin:13px;text-align:center;width:210px;border-radius:2px;}
.userBox .column li a {display:block;height:100%;text-decoration:none;width:100%;}
.userBox .avatar-big {display:none;}

/* A - Boite de connexion */
/* A1 - Boite de connexion - Partie haute*/
.loginBox{overflow:hidden;margin:0;top: calc(25% - 97px);left:calc(50% - 290px);padding:10px;padding-top:30px;width:560px;height:350px;}
.loginBox #loginform{width:330px;margin-left:90px;text-align:right;padding-top:40px;}
.loginBox #loginform .userInput, .loginBox #loginform .passInput{-moz-appearance:none !important;height:40px;width:180px;padding:5px 10px;margin-bottom:10px;font-size:16px;color:#555;background:#EDEDED;border:1px solid #EDEDED}
.loginBox #loginform .username, .loginBox #loginform .password{margin-right:15px;color:#444;}
.loginBox #loginform .loginbtn {-moz-appearance:none !important;width:200px;height:40px;margin:20px 40px 10px;padding:12px;font-size:13px;border-radius:2px;}
.loginBox #loginform .rememberMe{margin-left:140px;margin-top:4px;}
.loginBox #loginform .remMe{margin-top:0;}
/* A2 - Boite de connexion - Partie basse*/
.loginBox #loginform .register{width:580px;margin-top:40px;margin-right:-150px;height:70px;text-align:center;padding:0;}
.loginBox #loginform .register li{float:none;margin-top:10px;border-left:none;}
.loginBox #loginform .register li a{font-size:14px;display:inline-block;padding:5px;}
.loginBox #loginform .register li:first-of-type:before{content:'Vous n\'avez pas encore de compte ? \00a0';font-size:14px;}
/* A - FIN - Boite de connexion */

/** Fin du Menu personnel **/

/* headWrapper *******************************************************************/
.headerPlace .headWrapper{padding:0;background:none;box-shadow:none;background: none;}
.headerPlace .headWrapper .head{margin:0;width:100%;padding:0;}


.headerPlace .headWrapper .head .logo{z-index:10000;}
.headerPlace .headWrapper .head .logo a{width:100%;background:url(http://www.t411.ai/themes/blue/images/logo.png) no-repeat 16px 16px;background-size:70%;}

.headerPlace .headWrapper .head .logo a, .footer .logos{filter:grayscale(1);-webkit-filter: grayscale(1);opacity: 0.8;}

/**  1- 2- 2- 2- Zone de recherche **/

.headerPlace .headWrapper .toolbar{top:0;position:absolute;right:80px;height:81px;z-index:10000;width:calc(100% - 121px - 80px);}
.headerPlace .headWrapper .toolbar .searchBar{height:81px;width:calc(100% - 383px);}
.headerPlace .headWrapper .toolbar .searchBar #bar-search input{padding:0;border-radius:0;padding:0 25px;font-size:1.5em;}

.headerPlace .headWrapper .toolbar .searchBar #bar-search .searchBtn, .headerPlace .headWrapper .toolbar .searchBar #bar-search .searchHistory, .headerPlace .headWrapper .toolbar .loginBar>span{margin:0;}
.headerPlace .headWrapper .toolbar .loginBarSide, .headerPlace .headWrapper .toolbar .loginBar .sep, .headerPlace .headWrapper .toolbar .loginBar>span:nth-child(3) a strong{display:none;}

.headerPlace .headWrapper .toolbar .loginBar{padding:0;width:383px;position:absolute;right:0;height:81px;}
.headerPlace .headWrapper .toolbar .loginBar>a{line-height:80px;padding:0;text-decoration:none;text-align:center;}
.headerPlace .headWrapper .toolbar .loginBar>span:last-child{width:80px;padding:0;font-size:0px;float:right;}

.headerPlace .headWrapper .toolbar .loginBar>span:nth-child(3){width:100px;position:absolute;right:81px;}
.headerPlace .headWrapper .toolbar .loginBar>span:nth-child(3) a{padding:0;display:block;width:100px;text-decoration:none;}
.headerPlace .headWrapper .toolbar .loginBar>span:nth-child(3) a.newpm strong{display:block;position:relative;top:7px;left:60px;height:30px;width:30px;background:white;border-radius:30px;text-align:center;line-height:30px;box-shadow:0 0 5px black;color:red;}

.headerPlace .headWrapper .toolbar .loginBar>span:nth-child(1){position:absolute;font-size:0px;border:none !important;z-index:1000;}
.headerPlace .headWrapper .toolbar .loginBar>span:nth-child(1) a{position:absolute;font-size:0px;display:block;width:20px;height:40px;left:372px;top:-20px;border-radius:30px;}
.headerPlace .headWrapper .toolbar .loginBar>span:nth-child(1) a:after{content:'X';position:absolute;font-size:15px;display:block;text-align:center;width:20px;top:20px;}

.headerPlace .headWrapper .toolbar .loginBar>span:nth-child(5){border:none!important;}
.headerPlace .headWrapper .toolbar .loginBar>span:nth-child(5) a.needvote{display:block;position:absolute;height:29px;width:100px;right:82px;top:50px;text-decoration:none;color:red;padding:0;border:none !important;}
.headerPlace .headWrapper .toolbar .loginBar>span:nth-child(5) a.needvote strong{display:block;position:relative;top:6px;left:12px;font-size:18px;text-align:center;width:40px;}

.headerPlace .headWrapper .toolbar .loginBar>span:nth-last-child(3){border:none!important;font-size:0px;}
.headerPlace .headWrapper .toolbar .loginBar>span:nth-last-child(3) strong{font-size:45px;position:absolute;left:0px;top:23px;width:200px;text-align:center;}
.headerPlace .headWrapper .toolbar .loginBar>span:nth-last-child(3) a{position:absolute;left:170px;top:40px;display:block;width:20px;height:20px;padding:0;}

.headerPlace .headWrapper .toolbar .loginBar>span:nth-last-child(5){text-align:center;font-size:0px;line-height:25px;}
.headerPlace .headWrapper .toolbar .loginBar>span:nth-last-child(5) strong{font-size:12px;padding:2px;}


.headerPlace .headWrapper .toolbar .dropdown.clearfix.searches{z-index:-10;top:70px;left:calc(100% - 470px);}
.headerPlace .headWrapper .toolbar .dropdown.clearfix.searches ul.dropdown-menu{box-shadow:0 0 30px rgba(0,0,0,0.5);padding:20px 0;border:0;}
.headerPlace .headWrapper .toolbar .dropdown.clearfix.searches ul.dropdown-menu li a{padding:5px 20px;text-decoration:none;}
.headerPlace .headWrapper .toolbar .dropdown.clearfix.searches ul.dropdown-menu li a:hover{background:#ddd;}

.ui-autocomplete{max-width:none;}



/* lavamenu */

.headWrapper #lavamenu:hover{padding-top:700px !important}

.headWrapper #lavamenu:before{position:absolute;width:80px;content:' ';top:570px;z-index:1000000000;left:calc(100% - 80px);background-size:cover;}

#lavamenu>li:nth-last-child(3){display:none !important;}

#lavamenu{
  display: flex !important;
  display: -webkit-flex;
  align-items: flex-end;
  -webkit-align-items: flex-end;
  justify-content: flex-start;
  -webkit-justify-content: flex-start;
  flex-wrap: wrap;
  -webkit-flex-wrap: wrap;
  flex-direction: column;
  -webkit-flex-direction: column;
  position:absolute !important;
  top:0px !important;
  background:white;
  background-size: cover;
  z-index:1000 !important;
  margin:0 !important;
  width:1200px !important;
  padding:60px calc(100% / 2 - 600px) !important;
  height:400px;
  box-shadow:0 0 50px rgba(0,0,0,0.4);
  transition: padding-top .5s;
  margin-top:-570px !important;}

#lavamenu>li{
  display:block !important;
  position:relative !important;
  background:none;
  border-radius:0;
  margin:0 !important;
  border:0 !important;
  width:260px;
  min-height:95px;
  left:-4000px;
  transition:left .0s .0s}

.headWrapper #lavamenu:hover>li{left:0px;transition:left .6s .1s ease}

#lavamenu>li>a{color:black;font-size:1.5em;}

#lavamenu>li:nth-child(2)>a,#lavamenu>li:nth-child(6)>a,#lavamenu>li:nth-child(8)>a,#lavamenu>li:nth-child(9)>a, #lavamenu>li ul>li a{border-radius:2px !important;}

#lavamenu>li:nth-child(2),#lavamenu>li:nth-child(6),#lavamenu>li:nth-child(9),#lavamenu>li:nth-child(8){order: 2;-webkit-order: 2}
#lavamenu>li:nth-child(3),#lavamenu>li:nth-child(4),#lavamenu>li:nth-child(5){border-right:1px solid rgba(0,0,0,0.1) !important;padding-right:25px !important;}
#lavamenu>li:nth-child(2)>a,#lavamenu>li:nth-child(6)>a,#lavamenu>li:nth-child(8)>a,#lavamenu>li:nth-child(9)>a{display:block;padding:20px;margin:31px 20px 0px;}

#lavamenu>li:nth-child(5) ul>li{display:block;padding:0px;margin:12px 20px;}
#lavamenu>li:nth-child(5) ul>li a{padding:5px !important;}
#lavamenu>li:nth-child(5) ul{padding-top:9px !important;}
#lavamenu>li:nth-child(4) ul>li{display:block;padding:0px;margin:8px 12px;width:104px !important;height:67px !important;float:left;}
#lavamenu>li:nth-child(4) ul>li a{display:block;width:calc(100% - 2 * 5px);height:calc(100% - 23px);white-space: normal;padding:23px 5px 0px;}
#lavamenu>li:nth-child(4) ul>li:nth-child(5){width:234px !important;}
#lavamenu>li:nth-child(4) ul>li:nth-last-child(1) a,#lavamenu>li:nth-child(4) ul>li:nth-last-child(2) a{padding-top:16px !important;padding-bottom:10px;}
#lavamenu>li:nth-child(4) ul{margin-top:13px !important;}
#lavamenu>li:nth-child(3) ul{margin-top:20px !important;display:flex !important;display: -webkit-flex !important;flex-wrap: wrap;justify-content: flex-start;align-items:baseline;-webkit-flex-wrap: wrap;-webkit-justify-content: flex-start;-webkit-align-items:baseline;height:340px !important;}
#lavamenu>li:nth-child(3) ul>li{white-space: normal;padding:0;}
#lavamenu>li:nth-child(3) ul>li a{padding: 9px 10px 5px;font-size:0px !important;}
#lavamenu>li:nth-child(3) ul>li a:before{font-size:14px !important;}

#lavamenu>li:nth-child(3) ul>li:nth-child(3){order:1;-webkit-order:1;}
#lavamenu>li:nth-child(3) ul>li:nth-child(2){order:2;-webkit-order:2;}
#lavamenu>li:nth-child(3) ul>li:nth-child(1){order:3;-webkit-order:3;}
#lavamenu>li:nth-child(3) ul>li:nth-child(9){order:4;-webkit-order:4;}
#lavamenu>li:nth-child(3) ul>li:nth-child(4){order:5;-webkit-order:5;}
#lavamenu>li:nth-child(3) ul>li:nth-child(4) a{margin-left:12px;border-radius:/*[[radius]]*/ 0 0 /*[[radius]]*/ !important;}
#lavamenu>li:nth-child(3) ul>li:nth-child(5){order:6;-webkit-order:6;}
#lavamenu>li:nth-child(3) ul>li:nth-child(5) a{border-radius:0 !important;border-left:none !important;border-right:none !important;}
#lavamenu>li:nth-child(3) ul>li:nth-child(6){order:7;-webkit-order:7;}
#lavamenu>li:nth-child(3) ul>li:nth-child(6) a{border-radius:0px /*[[radius]]*/ /*[[radius]]*/ 0px !important}
#lavamenu>li:nth-child(3) ul>li:nth-child(8){order:8;-webkit-order:8;}
#lavamenu>li:nth-child(3) ul>li:nth-child(7){order:9;-webkit-order:9;}
#lavamenu>li:nth-child(3) ul>li:nth-child(3) a:before{content:'Rechercher';}
#lavamenu>li:nth-child(3) ul>li:nth-child(2) a:before{content:'Mots-clés';}
#lavamenu>li:nth-child(3) ul>li:nth-child(1) a:before{content:'Demandes';}
#lavamenu>li:nth-child(3) ul>li:nth-child(9) a:before{content:'Upload';}
#lavamenu>li:nth-child(3) ul>li:nth-child(4) a:before{content:'Jour';}
#lavamenu>li:nth-child(3) ul>li:nth-child(5) a:before{content:'Semaine';}
#lavamenu>li:nth-child(3) ul>li:nth-child(6) a:before{content:'Mois';}
#lavamenu>li:nth-child(3) ul>li:nth-child(8) a:before{content:'Rapides';}
#lavamenu>li:nth-child(3) ul>li:nth-child(7) a:before{content:'Non Seedés';}

#lavamenu>li:nth-child(3) ul>li:nth-child(3),#lavamenu>li:nth-child(3) ul>li:nth-child(2),#lavamenu>li:nth-child(3) ul>li:nth-child(1),#lavamenu>li:nth-child(3) ul>li:nth-child(9){width:104px;margin:1px 12px 15px;height:67px;}
#lavamenu>li:nth-child(3) ul>li:nth-child(3) a,#lavamenu>li:nth-child(3) ul>li:nth-child(2) a,#lavamenu>li:nth-child(3) ul>li:nth-child(1) a,#lavamenu>li:nth-child(3) ul>li:nth-child(9) a{line-height:49px;}
#lavamenu>li:nth-child(3) ul>li:nth-child(4) a,#lavamenu>li:nth-child(3) ul>li:nth-child(5) a,#lavamenu>li:nth-child(3) ul>li:nth-child(6) a{width:56px !important;margin-top:60px;}
#lavamenu>li:nth-child(3) ul>li:nth-child(8),#lavamenu>li:nth-child(3) ul>li:nth-child(7){width:104px;margin:16px 12px 20px;}
#lavamenu>li:nth-child(3) ul>li:nth-child(4):before{content:'Tops';display:block;width:100px;height:30px;position:absolute;margin-top:20px;margin-left:75px;text-shadow:none !important;font-size: 1.3em;}

#lavamenu ul{
  display:block !important;
  width:260px !important;
  height:350px !important;
  opacity:1 !important;
  padding:0 !important;
  position:relative !important;
  top:auto !important;
  left:auto !important;
  border-radius:0 !important;
  border:0 !important;
  background: none !important;
}

#lavamenu>li:nth-child(8) ul{display:none !important;}

#lavamenu ul>li{border:0 !important;}
#lavamenu>li.backLava{display:none !important;}


/* Wrapper ************************************************************************/
.wrapper{min-width:0;margin:0;}
.wrapper .contentWrapper{min-width:0;max-width: none;margin:0;}
.wrapper .contentWrapper>.content{margin:0;padding:0;max-width:none;min-width:0;}
.wrapper .contentWrapper>.content .block>h2{margin:0;padding:0;border:0;border-radius: 0;box-shadow: none;background: /*[[colorlight]]*/;color:/*[[colorfontmenu]]*/;}
.wrapper .contentWrapper>.content .block>h2 span{padding:0;border:0;border-radius:0;background:none;margin:10px 20px;color:/*[[colorfontmenu]]*/}

/* Description des pages torrents *************************************************/
.block.description article{text-align:center!important;padding:10px!important;}
.block.description article .align-center{padding: 0!important;}
.block.torrentDetails .block.description img{max-height:400px;max-width:600px;width:auto!important;}


