/*  Shoutbox ***********************************************************************/
#shoutbox{background:white;}

/* Flou ****************************************************/
.headerPlace.active .headWrapper, .headerPlace.active ~ .wrapper{-webkit-filter:blur(10px);filter:blur(10px);}

/* Footer ************************************************************************/
.footer{background:/*[[colorlight]]*/;}
.footer p {min-width:0px}
@media (max-width: 800px) {.footer .stats{display:none;}}
@media (max-width: 600px) {.footer .logos{display:none;}}

}
