@-moz-document domain("anime.plus") {
/*MainBody*/

    /* background images */
    /*
       Plain Blue: http://dl.dropboxusercontent.com/s/e4i2gq7p29dayd6/bg_blank.png
       Angel Beats: http://dl.dropboxusercontent.com/s/ayo339ker2zejd6/bg-ab-yurippetenshi.png
       Amagami SS: http://dl.dropboxusercontent.com/s/6oh0tfcptisylao/bg-amagami.png
       Chuunibyou: http://dl.dropboxusercontent.com/s/psefr6vrtxi4t0x/bg-chuu.png
       Date A Live (Kurumi): http://dl.dropboxusercontent.com/s/4n3pzfrp0x6eu76/bg-dal-kurumi.png
       Date A Live (Kurumi and Tohka): http://dl.dropboxusercontent.com/s/9mwf4c9mv1brmvy/bg.png
       Date A Live (Origami): http://dl.dropboxusercontent.com/s/wwkvmq6e9sdvb1b/bg-dal-origami.png
       Gears: http://dl.dropboxusercontent.com/s/hfzq201e1zteavo/bg-gears.png
       Inori Aizawa: http://dl.dropboxusercontent.com/s/03k6kx7jpbhyxeh/bg-inori.png
       Hyperdimension Neptunia: http://dl.dropboxusercontent.com/s/qe251bapic29oaf/bg-neptunia.png
       SAO: http://dl.dropboxusercontent.com/s/tn7xdx294vhtvpt/bg-sao-asunakirito.png
       Shakugan no Shana: http://dl.dropboxusercontent.com/s/zpmfvnp2zl3w2yl/bg-shakugan-shana.png
       To Love Ru: http://dl.dropboxusercontent.com/s/5geo41b34qf0zd1/bg-toloveru-kotegawayami.png
    */


body {
background-image: url("/*[[background]]*/") !important;
background-color: /*[[backgroundcolor]]*/!important;
background-repeat: no-repeat!important;
background-size: 100%!important;
background-origin: content-box;
background-position: center top!important;
}

.main-wrapper{
background: rgba(255, 255, 255, 0);
}

#menu ul li.active a {
color: white;
}

#menu ul li a {
display: block;
color: white;
}

#menu {
background: top right repeat-y linear-gradient(to left, rgba(255, 255, 255, 0) 0, rgba(255, 255, 255, 0) 8px);
box-shadow: -1px 0 0 #FFF inset;
}

#menu ul li.active a {
background: rgba(255, 255, 255, 0);
border-top: 1px solid rgba(221, 221, 221, 0);
border-bottom: 1px solid rgba(221, 221, 221, 0);
color: white;
}

#menu:before {
background: linear-gradient(to bottom, rgba(255, 255, 255, 0) 0, rgba(255, 255, 255, 0) 16px);
}

#menu:after {
background: linear-gradient(to top, rgba(255, 255, 255, 0) 0, rgba(255, 255, 255, 0) 16px);
}

h2, h3 {
color: white;
}

.main-wrapper {
Color: white;
}

h2:after, h3:after {
border-top: 1px solid #FFF;
background: center bottom no-repeat radial-gradient(rgba(255, 255, 255, 0), rgba(255, 255, 255, 0) 70%);
}

/*sidebar*/

#menu i {
background-color: rgba(136, 136, 136, 0);
opacity: .75;
}

#menu .anime i {
background-color: rgba(91, 148, 218, 0);
}

#menu .manga i {
background-color: rgba(235, 164, 120, 0);
}



#showcase i[class^='icon-'] {
background-color: rgba(170, 170, 170, 0);
}

/*HoverEffects*/

#menu ul li:not(.active) a:focus, #menu ul li:not(.active) a:hover {
border-right: 3px solid #DDD;
padding-right: 9px;
background-color: rgb(65, 64, 64);
text-decoration: none;
}

table.tablesorter tbody tr:hover td {
background: #353535;
}

/*ListTable*/

table.tablesorter thead tr .header {
background-color: rgb(53, 53, 53)!important;
cursor: pointer;
}

table.tablesorter thead th {
background-color: rgba(245, 245, 245, 0.46);
padding: 0.5em 6px 0.5em 21px;
border-right: 1px solid white;
}

.missing table thead tr th {
padding: 0.5em 1em;
background: rgb(53, 53, 53);
width: 50%;
}

.missing table tbody:hover tr td {
background: rgb(53, 53, 53);
}

/*header*/

#header {
padding: 0.78em 0;
margin: 0 0 200px 0!important;
border-bottom: 1px solid rgba(0, 0, 0, 1);
font-size: 115%;
background: /*[[header]]*/ repeat-x bottom!important;
}

.notice {
  padding: 0px!important;
  font-size: 0px!important;
}

/*Text*/

text {
fill: white!important;
}

/*menu*/

#showcase {
height: 13cm!important;
}

#start:before {
background: linear-gradient(to right, rgba(0, 0, 0, 1) 0%, rgba(255,255,255,0) 100%);
}

#start:after {
background: linear-gradient(to left, rgba(0, 0, 0, 1) 0%, rgba(255,255,255,0) 100%);
}

#start {
background: #000000;
box-shadow: 0 0 5px #FFF inset;
}






}
