css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("wikipedia.org") {

  body, #mw-page-base, .mw-body {
   background-color: rgb(28, 33, 38);
    color: #AFB7B5;
  }
  
  #mw-page-base {
    background-color: rgb(28, 33, 38);
    background-image: linear-gradient(#1B2026 50%, #1C2126 100%);
  }
  
  .mw-body {
    background-color: #343C42;
  }
  
  a {
    color: #2CAAB4;
  }
  
  div.vectorTabs ul li {
    background-image: linear-gradient(#1C2126 30%, #293039 100%);
  }
  
  div.vectorTabs li.selected {
    background-image: linear-gradient(#293039 10%, #343C42 100%);
    
  }
  
  div.vectorTabs li.selected a, div.vectorTabs li.selected a:visited {
    color: #2CAAB4;
  }
  
  h1, h2, h3, h4, h5, h6 {
    color: #FFF;
  }
  
  #toc, .toc, .mw-warning, .infobox {
    background-color: rgb(28, 33, 38);
  }
  
  .infobox {
    color: unset;
  }
  
  div#simpleSearch {
    background-color: rgb(23, 27, 31);
    background-image: none;
  }
  
  div#simpleSearch input {
    color: rgb(138, 140, 142);
  }
  
  table.ambox {
    background: none repeat scroll 0% 0% #1C2126;
  }
  
  .navbox, .navbox-subgroup {
    background: none repeat scroll 0% 0% #1C2126;
  }
  
  .navbox th, .navbox-title {
    background: none repeat scroll 0% 0% #293039;
  }
  
  .catlinks {
    background-color: rgb(28, 33, 38);
  }
}
