css:
@-moz-document domain("boards.na.leagueoflegends.com") {
.box.main, .reply.form-container .bottom-bar,
.edit.form-container .bottom-bar, /**Post editor**/
.edit.form-container .preview.box, /**Show preview small box **/ 
.reply.form-container .preview.box, .edit.form-container .preview.box /**Show preview small box in reply**/ 

{  
  background-color: /*[[darkest_main_color]]*/; 
  border-color: /*[[dark_main_color]]*/;
}
  
.reply.form-container .preview.box .live-preview-container,
.edit.form-container .preview.box .live-preview-container /**Previewed text**/ 
{
  border-color: /*[[very_light_main_colo]]*/;
}

.comment-list-item .parent-comment /**The header of notification entries **/
{
  background-color: /*[[light_darkest_main_c]]*/; 
  border-color: /*[[secondary_color]]*/;
}
/**Comments**/
/*Odd (light)*/
.nested-comment .list .nested-comment,
.nested-comment .list .nested-comment .list .nested-comment .list .nested-comment,
.nested-comment .list .nested-comment .list .nested-comment .list .nested-comment .list .nested-comment .list .nested-comment
{
  background-color: /*[[light_darkest_main_c]]*/; 
  border-color: /*[[average_main_color]]*/;
  color: /*[[light_secondary_colo]]*/!important;
}
/*Even (dark) */
.nested-comment,
.nested-comment .list .nested-comment .list .nested-comment,
.nested-comment .list .nested-comment .list .nested-comment .list .nested-comment .list .nested-comment {
  background-color: /*[[darkest_main_color]]*/; 
  border-color: /*[[dark_main_color]]*/;
  color: /*[[secondary_color]]*/;
}
  
/**OP usernames**/
.nested-comment.isOP > .body-container > .header > .inline-profile .username {
  background-color: /*[[average_main_color]]*/!important;
}
  

.discussion-list-item td, /**Separator between threads**/
.comments-header-container header /**Separator between OP adn replies**/
{
  border-bottom-color: /*[[dark_main_color]]*/;
}

.discussions.sticky,
.reply.form-container .textarea-holder,
.edit.form-container .textarea-holder,
input.box, textarea.box, /**another textarea crap**/
#submit input.box, #submit textarea.box,
.dropdown a.btn span, /** Board Select box when making new post - the clickabe area**/
.dropdown-menu /** Board Select box when making new post - the area with options**/
{  
  background-color: /*[[light_darkest_main_c]]*/; 
  border-color: /*[[light_main_color]]*/;
}  
/**Reply textarea needs different borders, otherwise it blends **/
.reply.form-container.main .textarea-holder
{
  background-color: /*[[light_darkest_main_c]]*/; 
  border-color: /*[[light_main_color]]*/;
  border-bottom-color: /*[[almost_dark_main_col]]*/;
}
.reply.form-container.main .bottom-bar /**reply - Post editor**/
{
  background-color: /*[[light_darkest_main_c]]*/; 
  border-color: /*[[light_main_color]]*/;

}


.reply.form-container .bottom-bar, .edit.form-container .bottom-bar {
  border-color: /*[[light_main_color]]*/;
  border-top: none;
}
.reply.form-container .textarea-holder, .edit.form-container .textarea-holder {  
  border-bottom-color: /*[[almost_dark_main_col]]*/;
}  
  
.box.show-more, #discussion .masthead {
  border-color: /*[[light_main_color]]*/;
  
}
  
.pvpnet-region-switcher .current-region span, .riot-apollo.pvpnet-id-card a, body .byline {
  color: /*[[light_main_color]]*/;
}
body .byline, 
.comments-header-container header .num-comments label,
.comments-header-container header .sorting,
.reply.form-container .preview.box, .edit.form-container .preview.box /**"Show preview" box text**/
  
{
  color: /*[[almost_dark_main_col]]*/!important;
}
  
}
