@-moz-document domain("theoldreader.com") {

/*                                                   
 *   __ __     _____ _   _ _____           _         
 *  |  |  |___|     | |_| | __  |___ ___ _| |___ ___ 
 *  |_   _| -_|  |  | | . |    -| -_| .'| . | -_|  _|
 *    |_| |___|_____|_|___|__|__|___|__,|___|___|_|  
 *                       Version 2.0.2 - Fork No More
 *                                          hippyjake
 *
 */
 html { overflow:hidden !important; }
 
.navbar .brand {
  background-image: url("/*[[logo]]*/");
}
body, .EmbeddedTweet-tweet {
    background-color: /*[[background]]*/;
    /*
    background-image: url(data:image/svg+xml;base64,);
    */
    color: /*[[textcolor]]*/;
    font: 13px/20px Helvetica Neue, Helvetica, Arial, sans-serif;
    margin: 0
}
a.buy-now {
    color: /*[[highlight]]*/ !important
}
.btn {
    -moz-box-shadow: none;
    -webkit-box-shadow: none;
    background: /*[[button-bg]]*/;
    border: 1px solid transparent!important;
    box-shadow: none;
    color: /*[[labletext]]*/;
    text-shadow: 0 1px 1px #000
}
.btn:focus,
.btn:hover {
    -moz-transition: background-position .1s linear;
    -o-transition: background-position .1s linear;
    -webkit-transition: background-position .1s linear;
    background: /*[[highlight]]*/;
    background-position: 0 -15px;
    color: /*[[labletext]]*/;
    text-decoration: none;
    transition: background-position .1s linear
}
.btn.active,
.btn:active {
    -moz-box-shadow: inset 0 2px 4px rgba(0, 0, 0, .15), 0 1px 2px rgba(0, 0, 0, .05);
    -webkit-box-shadow: inset 0 2px 4px rgba(0, 0, 0, .15), 0 1px 2px rgba(0, 0, 0, .05);
    background: /*[[highlight]]*/;
    box-shadow: inset 0 2px 4px rgba(0, 0, 0, .15), 0 1px 2px rgba(0, 0, 0, .05);
    color: /*[[labletext]]*/;
    outline: 0
}
.badge,
.badge.badge-info,
.btn-group.open .btn.dropdown-toggle,
.label.label-feed {
    background-color: /*[[highlight]]*/;
    border: 1px solid /*[[highlight]]*/;
    color: /*[[labletext]]*/
}
.dropdown-menu {
    -moz-border-radius: 6px;
    -moz-box-shadow: 0 1px 1px rgba(0, 0, 0, .2);
    -webkit-border-radius: 6px;
    -webkit-box-shadow: 0 1px 1px rgba(0, 0, 0, .2);
    background-color: /*[[background]]*/;
    border: /*[[background]]*/;
    border-radius: 6px;
    box-shadow: 0 1px 1px rgba(0, 0, 0, .2)
}
.dropdown-menu > li > a,
.home-page dl dd p a,
.label.label-transparent,
.nav-list li a,
.navbar .nav li a,
.post .content,
.post h3 a,
h1,
h2,
h3,
h4,
h5,
h6 {
    color: /*[[textcolor]]*/
}
.dropdown-menu > li > a:focus,
.dropdown-menu > li > a:hover,
.dropdown-submenu:focus > a,
.dropdown-submenu:hover > a {
    background: /*[[highlight]]*/;
    color: /*[[textcolor]]*/;
    text-decoration: none
}
.caret {
    border-top: 4px solid #fff
}
.form-search .search-query {
    background: transparent;
    color: /*[[textcolor]]*/
}
.uneditable-input:focus,
form:focus,
input[type="datetime-local"]:focus,
input[type=color]:focus,
input[type=date]:focus,
input[type=datetime]:focus,
input[type=email]:focus,
input[type=month]:focus,
input[type=number]:focus,
input[type=password]:focus,
input[type=search]:focus,
input[type=tel]:focus,
input[type=text]:focus,
input[type=time]:focus,
input[type=url]:focus,
input[type=week]:focus,
textarea:focus {
    -moz-box-shadow: 0 0 20px 0 rgba(255, 255, 255, .75);
    -webkit-box-shadow: 0 0 20px 0 rgba(255, 255, 255, .75);
    border-color: rgba(255, 255, 255, .75);
    box-shadow: 0 0 20px 0 rgba(255, 255, 255, .75)
}
.uneditable-input,
input[type="datetime-local"],
input[type=color],
input[type=date],
input[type=datetime],
input[type=email],
input[type=month],
input[type=number],
input[type=password],
input[type=search],
input[type=tel],
input[type=text],
input[type=time],
input[type=url],
input[type=week],
textarea {
    -moz-transition: border linear .2s, box-shadow linear .2s;
    -o-transition: border linear .2s, box-shadow linear .2s;
    -webkit-transition: border linear .2s, box-shadow linear .2s;
    background-color: /*[[background]]*/;
    border: 0;
    color: /*[[textcolor]]*/;
    transition: border linear .2s, box-shadow linear .2s
}
.navbar .navbar-inner {
    background-color: rgba(0, 0, 0, .18);
    border-bottom: 1px solid rgba(48, 166, 124, .05)
}
.navbar .btn-navbar {
    background: /*[[button-bg]]*/;
    border: solid 1px transparent;
}
.navbar .btn-navbar .icon-bar {
    background: #fff
}
.navbar .btn-navbar:hover {
    background: /*[[highlight]]*/
}
.navbar .nav li .dropdown-menu {
    background: /*[[background]]*/;
    border-color: /*[[background]]*/;
    box-shadow: 0 1px 1px rgba(0, 0, 0, .2)
}
.nano > .pane > .slider {
    background: /*[[highlight]]*/
}
.home-page dl dd p a:hover,
.nav-list li a:focus:hover,
.nav-list li a:hover,
.nav-list li a:hover:hover,
.nav-list li.active a,
.nav-list li.active a:focus,
.nav-list li.active a:focus:hover,
.nav-list li.active a:hover,
.nav-list li.active a:hover:hover,
.navbar .nav > li > a:focus,
.navbar .nav > li > a:hover,
.navbar .nav li a:hover,
a,
a:focus,
a:hover {
    color: /*[[highlight]]*/
}
.reader .subscribe-fixed-top .bg {
    background: transparent!important
}
.reader .sidebar-fixed-top .well {
    -moz-border-radius-topleft: 20px;
    -moz-border-radius-topright: 20px;
    -webkit-border-top-left-radius: 20px;
    -webkit-border-top-right-radius: 20px;
    border: 1px solid rgba(0, 0, 0, .25);
    border-top-left-radius: 20px;
    border-top-right-radius: 20px
}
.subscribe .btn-primary {
    -moz-border-radius: 10px;
    -webkit-border-radius: 10px;
    background: /*[[button-bg]]*/;
    border: 1px solid #ebebeb;
    border-radius: 10px;
    box-shadow: 0 1px 2px #000;
    color: /*[[textcolor]]*/;
    text-shadow: none
}
.subscribe.open .btn-primary {
    background: /*[[highlight]]*/;
    color: /*[[textcolor]]*/
}
.hero-unit.posts-filler span,
.hero-unit,
.reader .floating {
    background: transparent
}
.well {
    background: rgba(0, 0, 0, .18);
    border: 1px solid rgba(0, 0, 0, .18)
}
.page-header {
    border-bottom: 1px solid transparent
}
.post.unread {
    border-left: 4px solid /*[[highlight]]*/
}
.post.unread .content {
    color: #888!important
}
.nav-list li.active a:focus:not(.i):before,
.nav-list li.active a:hover:not(.i):before,
.nav-list li.active a:not(.i):before {
    border-left: 3px solid /*[[highlight]]*/
}
.home-page .ourstuff a {
    color: #9d9d9d
}
.navbar .nav li.divider-vertical {
    background-color: transparent
}
.nav-list li.nav-header a,
.nav-list li.nav-header a:focus,
.nav-list li.nav-header a:hover {
    color: #cfcfcf !important
}
.post img {
    /*display: block !important;*/
    padding: 5px 5px 5px 0 !important
}
.modal{
	background: /*[[background]]*/;
}
.modal-backdrop {
    background-color: /*[[background]]*/;
}
.close {
    color: #FFF;
}

