css:
@-moz-document domain("tumblr.com") {
.identity-refresh .selected .tab_notice, .identity-refresh .inbox_notice_container, .identity-refresh .new_post_notice_container {
background: #D95E3F;
-webkit-animation: rainbow 15s infinite alternate;
}

.identity-refresh .tab_notice .tab_notice_value {
color:#FFF;
}

/*Firefox*/
@-moz-keyframes rainbowe {
          0% {background-color: #ffc0c0;}
     15% {background-color: #ffc0e7;}
     30% {background-color: #e7c0ff;}
     45% {background-color: #c0c0ff;}
     60% {background-color: #c0dfff;}
     75% {background-color: #c0fffa;}
     90% {background-color: #c0ffd0;}
     100% {background-color: #ffdec0;}
}

/*Safari and Chrome*/
@-webkit-keyframes rainbow {
      0% {background-color: #ffc0c0;}
     15% {background-color: #ffc0e7;}
     30% {background-color: #e7c0ff;}
     45% {background-color: #c0c0ff;}
     60% {background-color: #c0dfff;}
     75% {background-color: #c0fffa;}
     90% {background-color: #c0ffd0;}
     100% {background-color: #ffdec0;}
}
}
