@-moz-document 
domain("yahoo.com"),
domain("yahoo.co.jp"),
domain("thepostgame.com"),
domain("flickr.com"),
domain("flickr.net"),
domain("yimg.com") {

html, .rapidnofollow, .Answers,
.limit, .answer, .first-child, body,
.Row, #Stencil.Answers #UH, div, .Col2,
.ask-widget, .Bxz-bb, .Bgc-grey-light, .Sticky,
#Stencil .Answers .Bgc-grey-light, #Stencil .Bgc-grey-light,
.Bdstart-1g, .ya-discover-tile, .rapidnofollow,
#Stencil.Answers .UHCol2, #Stencil.Answers .Col2,
#Stencil.Answers.has-scrolled #UH,
#Stencil.Answers.Sticky-on .Col2 .Sticky
{
background: #000 !important;
}
#Stencil .Mx-a, input, textarea
{
background-color: #999 !important;
}
input, textarea
{
color: #000 !important;
}
div, a
{
color: #FFF !important;
}
}
